import React, { useState } from 'react';
import LeadForm from './components/LeadForm';
import LeadList from './components/LeadList';

const App = () => {
  const [leads, setLeads] = useState([]); // State to store the list of leads

  // Function to handle a newly created lead
  const handleLeadCreated = (newLead) => {
    setLeads((prevLeads) => [...prevLeads, newLead]); // Add the new lead to the list
  };

  return (
    <div>
      <h1>CRM Dashboard</h1>
      {/* Pass the handleLeadCreated function to LeadForm */}
      <LeadForm onLeadCreated={handleLeadCreated} />
      {/* Pass the current list of leads to LeadList */}
      <LeadList leads={leads} />
    </div>
  );
};

export default App;
