import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ContactDetailsModal.css';

const ContactDetailsModal = ({ contact, onClose }) => {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/tasks?contactId=${contact._id}`);
        setTasks(response.data);
      } catch (error) {
        console.error('Failed to fetch tasks:', error);
      }
    };

    fetchTasks();
  }, [contact._id]);

  const handleDeleteContact = async () => {
    if (window.confirm('Are you sure you want to delete this contact?')) {
      try {
        await axios.delete(`http://localhost:5000/api/contacts/${contact._id}`);
        onClose(); // Close the modal after deletion
      } catch (error) {
        console.error('Failed to delete contact:', error);
      }
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button className="close-button" onClick={onClose}>
          âœ–
        </button>
        <h1>Contact Details</h1>

        <div className="lead-overview">
          <h2>{contact.name}</h2>
          <p>Email: <a href={`mailto:${contact.email}`}>{contact.email}</a></p>
          <p>Phone: <a href={`tel:${contact.phone}`}>{contact.phone}</a></p>
          <p>Company: {contact.company || 'N/A'}</p>
          <p>Position/Role: {contact.position || 'N/A'}</p>
        </div>

        <div className="lead-details-section">
          <h3>Details</h3>
          <p>Lead Source: {contact.source || 'N/A'}</p>
          <p>Status: {contact.status || 'N/A'}</p>
          <p>Priority: {contact.priority || 'N/A'}</p>
        </div>

        <div className="notes-section">
          <h3>Notes</h3>
          <p>{contact.notes || 'No notes available.'}</p>
        </div>

        <div className="recent-activity">
          <h3>Recent Activity</h3>
          {contact.recentActivity && contact.recentActivity.length > 0 ? (
            <ul>
              {contact.recentActivity.map((activity, index) => (
                <li key={index}>{activity}</li>
              ))}
            </ul>
          ) : (
            <p>No recent activity.</p>
          )}
        </div>

        <div className="tasks-section">
          <h3>Tasks</h3>
          {tasks.length > 0 ? (
            <ul>
              {tasks.map((task) => (
                <li key={task._id}>
                  {task.title} - {task.status} (Due: {task.dueDate})
                </li>
              ))}
            </ul>
          ) : (
            <p>No tasks found.</p>
          )}
          <a href={`/tasks/add?contactId=${contact._id}`} className="add-task-link">
            Add Task
          </a>
        </div>

        <div className="action-buttons">
          <button onClick={handleDeleteContact}>Delete Contact</button>
          <button onClick={onClose}>Close</button>
        </div>
      </div>
    </div>
  );
};

export default ContactDetailsModal;
