import React, { useState } from 'react';
import axios from 'axios';

const MailForm = () => {
  const [formData, setFormData] = useState({
    to: '',
    subject: '',
    text: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/mail/send', formData);
      alert('Mail sent successfully!');
      setFormData({ to: '', subject: '', text: '' });
    } catch (error) {
      alert('Failed to send mail');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Send Email</h2>
      <input name="to" value={formData.to} onChange={handleChange} placeholder="Recipient Email" />
      <input name="subject" value={formData.subject} onChange={handleChange} placeholder="Subject" />
      <textarea name="text" value={formData.text} onChange={handleChange} placeholder="Message" />
      <button type="submit">Send Email</button>
    </form>
  );
};

export default MailForm;
