import React, { useState, useEffect } from 'react';
import './ContactList.css';
import EmailSummary from '../Email/EmailSummary'; // Adjust import path for EmailSummary
import EmailForm from '../Email/EmailForm'; // Adjust import path for EmailForm
import EmailList from '../Email/EmailList'; // Adjust import path for EmailList
import axios from 'axios';

const ContactList = () => {
  const [emails, setEmails] = useState([]); // State for storing emails
  const [selectedClientId, setSelectedClientId] = useState(null); // State for selected client ID

  // Fetch emails when client is selected
  useEffect(() => {
    if (selectedClientId) {
      const fetchEmails = async () => {
        try {
          const response = await axios.get(`http://localhost:5000/api/emails/client/${selectedClientId}`);
          setEmails(response.data);
        } catch (error) {
          console.error('Failed to fetch emails:', error);
        }
      };
      fetchEmails();
    }
  }, [selectedClientId]);

  // Handle client selection
  const handleClientSelection = (clientId) => {
    setSelectedClientId(clientId); // Update selected client ID
  };

  return (
    <div className="contact-list">
      <h1>Contact List</h1>

      {/* Display the email summary when a client is selected */}
      {selectedClientId && <EmailSummary emails={emails} />}

      {/* Contact list table */}
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Phone</th>
            <th>Email</th>
            <th>Last Interaction</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {/* Example contact list with selectable client ID */}
          <tr>
            <td>John Doe</td>
            <td>(123) 456-7890</td>
            <td>johndoe@example.com</td>
            <td>Yesterday</td>
            <td>
              <button onClick={() => handleClientSelection(1)}>View Emails</button>
            </td>
          </tr>
          <tr>
            <td>Jane Smith</td>
            <td>(987) 654-3210</td>
            <td>janesmith@example.com</td>
            <td>Last Week</td>
            <td>
              <button onClick={() => handleClientSelection(2)}>View Emails</button>
            </td>
          </tr>
        </tbody>
      </table>

      {/* Show the email form and list for the selected client */}
      {selectedClientId && <EmailForm clientId={selectedClientId} />}
      {selectedClientId && <EmailList clientId={selectedClientId} />}
    </div>
  );
};

export default ContactList;
