import React, { useState, useEffect } from 'react';
import './Dashboard.css';
import { getContacts } from '../api/contactApi'; // API function to fetch contacts
import { getTasks } from '../api/taskApi'; // API function to fetch tasks
import { Bar } from 'react-chartjs-2'; // Import the Bar chart component from react-chartjs-2
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js'; // Import chart.js components

// Register chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const Dashboard = () => {
  const [totalContacts, setTotalContacts] = useState(0);
  const [upcomingTasks, setUpcomingTasks] = useState([]);
  const [leadCount, setLeadCount] = useState(0);
  const [contactCount, setContactCount] = useState(0);
  const [taskStatusCounts, setTaskStatusCounts] = useState({ pending: 0, inProgress: 0, completed: 0 });

  // Fetch data when the component loads
  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch contacts
        const contacts = await getContacts();
        setTotalContacts(contacts.length);

        // Count leads and contacts based on the 'type'
        const leads = contacts.filter(contact => contact.type === 'lead');
        const contactsCount = contacts.filter(contact => contact.type === 'contact');
        setLeadCount(leads.length);
        setContactCount(contactsCount.length);

        // Fetch tasks
        const tasks = await getTasks();
        const upcoming = tasks.filter(
          (task) => new Date(task.dueDate) > new Date() // Filter tasks due in the future
        );
        setUpcomingTasks(upcoming.slice(0, 5)); // Show only the next 5 tasks

        // Count task statuses
        const statusCounts = tasks.reduce(
          (acc, task) => {
            if (task.status === 'Pending') acc.pending++;
            else if (task.status === 'In Progress') acc.inProgress++;
            else if (task.status === 'Completed') acc.completed++;
            return acc;
          },
          { pending: 0, inProgress: 0, completed: 0 }
        );
        setTaskStatusCounts(statusCounts);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []); // This effect runs only once when the component mounts

  // Data for the lead vs contact ratio chart
  const leadContactChartData = {
    labels: ['Leads', 'Contacts'],
    datasets: [
      {
        label: 'Leads vs Contacts',
        data: [leadCount, contactCount], // Update with dynamic lead and contact counts
        backgroundColor: ['#4CAF50', '#FF5722'], // Different colors for leads and contacts
        borderColor: ['#388E3C', '#D32F2F'],
        borderWidth: 1,
      },
    ],
  };

  // Data for task status chart
  const taskStatusChartData = {
    labels: ['Pending', 'In Progress', 'Completed'],
    datasets: [
      {
        label: 'Task Status',
        data: [taskStatusCounts.pending, taskStatusCounts.inProgress, taskStatusCounts.completed], // Update with dynamic task status counts
        backgroundColor: ['#FF9800', '#2196F3', '#4CAF50'], // Different colors for each status
        borderColor: ['#F57C00', '#1976D2', '#388E3C'],
        borderWidth: 1,
      },
    ],
  };

  // Options for the charts
  const chartOptions = {
    responsive: true,
    plugins: {
      title: {
        display: true,
        text: 'Lead vs Contact Ratio',
      },
      tooltip: {
        enabled: true,
      },
    },
  };

  return (
    <div className="dashboard">
      <h1>Welcome to Your CRM Dashboard</h1>
      <div className="stats">
        <div className="stat">
          <h2>Total Contacts</h2>
          <p>{totalContacts}</p>
        </div>
        <div className="stat">
          <h2>Upcoming Tasks</h2>
          {upcomingTasks.length > 0 ? (
            <ul>
              {upcomingTasks.map((task, index) => (
                <li key={index}>
                  {task.title} - Due {new Date(task.dueDate).toLocaleDateString()}
                </li>
              ))}
            </ul>
          ) : (
            <p>No upcoming tasks</p>
          )}
        </div>
      </div>

      {/* Bar chart for lead vs contact ratio */}
      <div className="chart-container">
        <h2>Lead vs Contact Ratio</h2>
        <Bar data={leadContactChartData} options={chartOptions} />
      </div>

      {/* Bar chart for task status */}
      <div className="chart-container">
        <h2>Task Status Distribution</h2>
        <Bar data={taskStatusChartData} options={chartOptions} />
      </div>
    </div>
  );
};

export default Dashboard;
