// App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Header from './components/Header';
import Dashboard from './components/Dashboard';
import ContactList from './components/Contacts/ContactList';
import TaskManager from './components/Tasks/TaskManager';
import Settings from './components/Settings';
import SignInPage from './components/SignIn'; // Import the sign-in page component
import './App.css';

const App = () => {
  const isAuthenticated = localStorage.getItem('isAuthenticated'); // Basic auth check

  return (
    <Router>
      {isAuthenticated && <Header />}
      <div className="container">
        <Routes>
          {!isAuthenticated ? (
            <Route path="*" element={<SignInPage />} /> // Redirect unauthenticated users to the sign-in page
          ) : (
            <>
              <Route path="/" element={<Dashboard />} />
              <Route path="/contacts" element={<ContactList />} />
              <Route path="/tasks" element={<TaskManager />} />
              <Route path="/settings" element={<Settings />} />
            </>
          )}
        </Routes>
      </div>
    </Router>
  );
};

export default App;
