// SignIn.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './SignIn.css'; // Assuming you'll add CSS styling to match the design

const SignInPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();

    if (username === 'admin' && password === 'password') {
      navigate('/'); // Redirect to dashboard
    } else {
      setError('Invalid username or password');
    }
  };

  return (
    <div className="signin-container">
      <div className="signin-box">
        {/* Left section with welcome message */}
        <div className="signin-welcome">
          <h2>Welcome Back!</h2>
          <p>Your journey to seamless client management starts here.</p>
          <img 
            src="/assets/welcome-illustration.png" 
            alt="Welcome Illustration" 
            className="welcome-image"
          />
        </div>

        {/* Right section with login form */}
        <div className="signin-form-section">
          <h3>Sign In</h3>
          {error && <p className="error-message">{error}</p>}
          <form onSubmit={handleLogin} className="signin-form">
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="signin-input"
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="signin-input"
            />
            <button type="submit" className="signin-button">Login</button>
          </form>

          {/* Additional links */}
          <div className="signin-links">
            <a href="/forgot-password" className="forgot-password">Forgot Password?</a>
            <a href="/signup" className="signup-link">Sign Up</a>
          </div>

          {/* Social sign-in buttons */}
          <div className="social-login">
            <button className="social-button google">Sign in with Google</button>
            <button className="social-button microsoft">Sign in with Microsoft</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SignInPage;
