import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './TaskManager.css';

const TaskManager = () => {
  const [tasks, setTasks] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [currentTask, setCurrentTask] = useState({
    title: '',
    dueDate: '',
    description: '',
    status: '',
  });
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/tasks');
        setTasks(response.data);
      } catch (error) {
        console.error('Failed to fetch tasks:', error);
      }
    };

    fetchTasks();
  }, []);

  const handleFormChange = ({ target: { name, value } }) => {
    setCurrentTask((prev) => ({ ...prev, [name]: value }));
  };

  const validateTask = () => {
    const { title, dueDate, status } = currentTask;
    if (!title || !dueDate || !status) {
      setError('All required fields must be filled out.');
      return false;
    }
    setError('');
    return true;
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    if (!validateTask()) return;

    try {
      const payload = {
        ...currentTask,
        dueDate: new Date(currentTask.dueDate).toISOString(), // Ensure ISO format
      };

      if (currentTask._id) {
        const response = await axios.put(
          `http://localhost:5000/api/tasks/${currentTask._id}`,
          payload
        );
        setTasks((prev) =>
          prev.map((task) => (task._id === response.data._id ? response.data : task))
        );
      } else {
        const response = await axios.post('http://localhost:5000/api/tasks', payload);
        setTasks((prev) => [...prev, response.data]);
      }
      resetForm();
    } catch (error) {
      console.error('Failed to save task:', error.response?.data || error.message);
    }
  };

  const resetForm = () => {
    setIsEditing(false);
    setCurrentTask({ title: '', dueDate: '', description: '', status: '' });
    setError('');
  };

  const handleAddTask = () => {
    setCurrentTask({ title: '', dueDate: '', description: '', status: '' });
    setIsEditing(true);
  };

  const handleTaskClick = (task) => {
    setCurrentTask({ ...task });
    setIsEditing(true);
  };

  const handleDeleteTask = async (id) => {
    if (window.confirm('Are you sure you want to delete this task?')) {
      try {
        await axios.delete(`http://localhost:5000/api/tasks/${id}`);
        setTasks((prev) => prev.filter((task) => task._id !== id));
      } catch (error) {
        console.error('Failed to delete task:', error);
      }
    }
  };

  return (
    <div className="task-manager">
      <h1>Task Manager</h1>
      <button onClick={handleAddTask} className="add-task-button">
        Add Task
      </button>

      {isEditing ? (
        <div className="task-form">
          <h2>{currentTask._id ? 'Edit Task' : 'Add Task'}</h2>
          {error && <p className="error-message">{error}</p>}
          <form onSubmit={handleFormSubmit}>
            <label>
              Title:
              <input
                type="text"
                name="title"
                value={currentTask.title}
                onChange={handleFormChange}
                required
              />
            </label>
            <label>
              Due Date:
              <input
                type="date"
                name="dueDate"
                value={currentTask.dueDate}
                onChange={handleFormChange}
                required
              />
            </label>
            <label>
              Description:
              <textarea
                name="description"
                value={currentTask.description}
                onChange={handleFormChange}
              />
            </label>
            <label>
              Status:
              <select
                name="status"
                value={currentTask.status}
                onChange={handleFormChange}
                required
              >
                <option value="">Select Status</option>
                <option value="Pending">Pending</option>
                <option value="In Progress">In Progress</option>
                <option value="Completed">Completed</option>
              </select>
            </label>
            <button type="submit">Save</button>
            <button type="button" onClick={resetForm}>
              Cancel
            </button>
          </form>
        </div>
      ) : tasks.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Title</th>
              <th>Due Date</th>
              <th>Description</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {tasks.map((task) => (
              <tr key={task._id}>
                <td onClick={() => handleTaskClick(task)}>{task.title}</td>
                <td onClick={() => handleTaskClick(task)}>{new Date(task.dueDate).toLocaleDateString()}</td>
                <td onClick={() => handleTaskClick(task)}>{task.description}</td>
                <td onClick={() => handleTaskClick(task)}>{task.status}</td>
                <td>
                  <button onClick={() => handleDeleteTask(task._id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No tasks found.</p>
      )}
    </div>
  );
};

export default TaskManager;
