import React, { useState, useEffect } from 'react';
import './ContactList.css';
import axios from 'axios';

const ContactList = () => {
  const [contacts, setContacts] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [currentContact, setCurrentContact] = useState({
    name: '',
    email: '',
    phone: '',
    company: '',
    position: '',
    notes: '',
    type: 'lead', // Default type
    leadScore: '', // Default empty
    lastInteraction: '',
  });
  const [sortOption, setSortOption] = useState('name'); // Default sorting by name

  // Fetch contacts from the API
  useEffect(() => {
    const fetchContacts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/contacts');
        setContacts(response.data);
      } catch (error) {
        console.error('Failed to fetch contacts:', error);
      }
    };

    fetchContacts();
  }, []);

  // Sort contacts based on the selected option
  const sortContacts = (contacts, criteria) => {
    const sorted = [...contacts];
    switch (criteria) {
      case 'name':
        return sorted.sort((a, b) => a.name.localeCompare(b.name));
      case 'email':
        return sorted.sort((a, b) => a.email.localeCompare(b.email));
      case 'type':
        return sorted.sort((a, b) => a.type.localeCompare(b.type));
      case 'lastInteraction':
        return sorted.sort((a, b) => new Date(a.lastInteraction) - new Date(b.lastInteraction));
      case 'createdDate':
        return sorted.sort((a, b) => new Date(a.createdDate) - new Date(b.createdDate));
      default:
        return sorted;
    }
  };

  // Update sorted contacts when sortOption or contacts change
  useEffect(() => {
    setContacts((prevContacts) => sortContacts(prevContacts, sortOption));
  }, [sortOption]);

  // Handle form input changes
  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setCurrentContact((prev) => ({ ...prev, [name]: value }));
  };

  // Handle form submission to add or update contact
  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      if (currentContact._id) {
        // Update existing contact
        const response = await axios.put(
          `http://localhost:5000/api/contacts/${currentContact._id}`,
          currentContact
        );
        setContacts((prev) =>
          prev.map((contact) => (contact._id === response.data._id ? response.data : contact))
        );
      } else {
        // Add new contact
        const response = await axios.post('http://localhost:5000/api/contacts', currentContact);
        setContacts((prev) => [...prev, response.data]);
      }
      setIsEditing(false);
      setCurrentContact({
        name: '',
        email: '',
        phone: '',
        company: '',
        position: '',
        notes: '',
        type: 'lead',
        leadScore: '',
        lastInteraction: '',
      });
    } catch (error) {
      console.error('Failed to save contact:', error);
    }
  };

  // Handle adding a new contact
  const handleAddContact = () => {
    setCurrentContact({
      name: '',
      email: '',
      phone: '',
      company: '',
      position: '',
      notes: '',
      type: 'lead',
      leadScore: '',
      lastInteraction: '',
    });
    setIsEditing(true);
  };

  // Handle clicking a contact to edit
  const handleContactClick = (contact) => {
    setCurrentContact(contact);
    setIsEditing(true);
  };

  // Handle deleting a contact
  const handleDeleteContact = async (id) => {
    if (window.confirm('Are you sure you want to delete this contact?')) {
      try {
        await axios.delete(`http://localhost:5000/api/contacts/${id}`);
        setContacts((prev) => prev.filter((contact) => contact._id !== id));
      } catch (error) {
        console.error('Failed to delete contact:', error);
      }
    }
  };

  // Handle sorting when a column header is clicked
  const handleSort = (column) => {
    setSortOption(column);
  };

  return (
    <div className="contact-list">
      <h1>Contact List</h1>
      <button onClick={handleAddContact} className="add-contact-button">Add Contact</button>

      {isEditing ? (
        <div className="contact-form">
          <h2>{currentContact._id ? 'Contact Details' : 'Add Contact'}</h2>
          <form onSubmit={handleFormSubmit}>
            <label>
              Name:
              <input
                type="text"
                name="name"
                value={currentContact.name}
                onChange={handleFormChange}
                required
              />
            </label>
            <label>
              Email:
              <input
                type="email"
                name="email"
                value={currentContact.email}
                onChange={handleFormChange}
                required
              />
            </label>
            <label>
              Phone:
              <input
                type="text"
                name="phone"
                value={currentContact.phone}
                onChange={handleFormChange}
              />
            </label>
            <label>
              Company:
              <input
                type="text"
                name="company"
                value={currentContact.company}
                onChange={handleFormChange}
              />
            </label>
            <label>
              Type:
              <select
                name="type"
                value={currentContact.type}
                onChange={handleFormChange}
                required
              >
                <option value="lead">Lead</option>
                <option value="contact">Contact</option>
              </select>
            </label>
            <label>
              Position:
              <input
                type="text"
                name="position"
                value={currentContact.position}
                onChange={handleFormChange}
              />
            </label>
            <label>
              Notes:
              <textarea
                name="notes"
                value={currentContact.notes}
                onChange={handleFormChange}
              />
            </label>
            <label>
              Lead Score:
              <input
                type="number"
                name="leadScore"
                value={currentContact.leadScore}
                onChange={handleFormChange}
                min="0"
                max="100"
              />
            </label>
            <label>
              Last Interaction:
              <input
                type="date"
                name="lastInteraction"
                value={currentContact.lastInteraction?.slice(0, 10)} // Format the date
                onChange={handleFormChange}
              />
            </label>
            <button type="submit">Save</button>
            <button type="button" onClick={() => setIsEditing(false)}>Cancel</button>
          </form>
        </div>
      ) : (
        <>
          {contacts.length > 0 ? (
            <table>
              <thead>
                <tr>
                  <th onClick={() => handleSort('name')}>Name</th>
                  <th onClick={() => handleSort('email')}>Email</th>
                  <th onClick={() => handleSort('phone')}>Phone</th>
                  <th onClick={() => handleSort('company')}>Company</th>
                  <th onClick={() => handleSort('type')}>Type</th>
                  <th onClick={() => handleSort('lastInteraction')}>Last Interaction</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {contacts.map((contact) => (
                  <tr key={contact._id}>
                    <td onClick={() => handleContactClick(contact)}>{contact.name}</td>
                    <td onClick={() => handleContactClick(contact)}>{contact.email}</td>
                    <td onClick={() => handleContactClick(contact)}>{contact.phone}</td>
                    <td onClick={() => handleContactClick(contact)}>{contact.company}</td>
                    <td onClick={() => handleContactClick(contact)}>{contact.type}</td>
                    <td onClick={() => handleContactClick(contact)}>{contact.lastInteraction}</td>
                    <td>
                      <button onClick={() => handleDeleteContact(contact._id)}>Delete</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p>No contacts found.</p>
          )}
        </>
      )}
    </div>
  );
};

export default ContactList;
