import React, { useState, useEffect } from 'react';
import './ContactList.css';
import axios from 'axios';

const ContactList = () => {
  const [contacts, setContacts] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [currentContact, setCurrentContact] = useState({
    name: '',
    phone: '',
    email: '',
    notes: '',
  });

  useEffect(() => {
    const fetchContacts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/contacts');
        setContacts(response.data);
      } catch (error) {
        console.error('Failed to fetch contacts:', error);
      }
    };

    fetchContacts();
  }, []);

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setCurrentContact((prev) => ({ ...prev, [name]: value }));
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      if (currentContact._id) {
        // Edit contact
        const response = await axios.put(
          `http://localhost:5000/api/contacts/${currentContact._id}`,
          currentContact
        );
        setContacts((prev) =>
          prev.map((contact) => (contact._id === response.data._id ? response.data : contact))
        );
      } else {
        // Add new contact
        const response = await axios.post('http://localhost:5000/api/contacts', currentContact);
        setContacts((prev) => [...prev, response.data]);
      }
      setIsEditing(false);
      setCurrentContact({ name: '', phone: '', email: '', notes: '' });
    } catch (error) {
      console.error('Failed to save contact:', error);
    }
  };

  const handleAddContact = () => {
    setCurrentContact({ name: '', phone: '', email: '', notes: '' });
    setIsEditing(true);
  };

  const handleContactClick = (contact) => {
    setCurrentContact(contact);
    setIsEditing(true);
  };

  const handleDeleteContact = async (id) => {
    if (window.confirm('Are you sure you want to delete this contact?')) {
      try {
        await axios.delete(`http://localhost:5000/api/contacts/${id}`);
        setContacts((prev) => prev.filter((contact) => contact._id !== id));
      } catch (error) {
        console.error('Failed to delete contact:', error);
      }
    }
  };

  // New method to send an email to the contact's email address
  const handleSendEmail = async (email) => {
    const subject = 'Hello from CRM';
    const body = 'This is a test email sent from your CRM application.';

    try {
      const response = await axios.post('http://localhost:5000/api/emails/send', {
        to: email,
        subject: subject,
        body: body,
      });
      alert('Email sent successfully');
      console.log(response.data); // Optional: log response data
    } catch (error) {
      console.error('Error sending email:', error);
      alert('Failed to send email');
    }
  };

  return (
    <div className="contact-list">
      <h1>Contact List</h1>
      <button onClick={handleAddContact} className="add-contact-button">Add Contact</button>

      {isEditing ? (
        <div className="contact-form">
          <h2>{currentContact._id ? 'Edit Contact' : 'Add Contact'}</h2>
          <form onSubmit={handleFormSubmit}>
            <label>
              Name:
              <input
                type="text"
                name="name"
                value={currentContact.name}
                onChange={handleFormChange}
                required
              />
            </label>
            <label>
              Phone:
              <input
                type="text"
                name="phone"
                value={currentContact.phone}
                onChange={handleFormChange}
                required
              />
            </label>
            <label>
              Email:
              <input
                type="email"
                name="email"
                value={currentContact.email}
                onChange={handleFormChange}
                required
              />
            </label>
            <label>
              Notes:
              <textarea
                name="notes"
                value={currentContact.notes}
                onChange={handleFormChange}
              />
            </label>
            <button type="submit">Save</button>
            <button type="button" onClick={() => setIsEditing(false)}>
              Cancel
            </button>
          </form>
        </div>
      ) : (
        <>
          {contacts.length > 0 ? (
            <table>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Phone</th>
                  <th>Email</th>
                  <th>Notes</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {contacts.map((contact) => (
                  <tr key={contact._id}>
                    <td onClick={() => handleContactClick(contact)}>{contact.name}</td>
                    <td onClick={() => handleContactClick(contact)}>{contact.phone}</td>
                    <td onClick={() => handleContactClick(contact)}>{contact.email}</td>
                    <td onClick={() => handleContactClick(contact)}>{contact.notes}</td>
                    <td>
                      <button onClick={() => handleDeleteContact(contact._id)}>Delete</button>
                      {/* Add a Send Email button */}
                      <button onClick={() => handleSendEmail(contact.email)}>
                        Send Email
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p>No contacts found.</p>
          )}
        </>
      )}
    </div>
  );
};

export default ContactList;
