import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Header from './components/Header';
import Dashboard from './components/Dashboard';
import ContactList from './components/Contacts/ContactList';
import TaskManager from './components/Tasks/TaskManager';
import Settings from './components/Settings';
import SignInPage from './components/SignIn'; // Import the sign-in page component
import './App.css';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Sync authentication state with localStorage
  useEffect(() => {
    const authState = localStorage.getItem('isAuthenticated') === 'true';
    setIsAuthenticated(authState);
  }, []);

  return (
    <Router>
      {isAuthenticated && <Header />} {/* Show the Header only if the user is authenticated */}
      <div className="container">
        <Routes>
          {/* If the user is not authenticated, show the sign-in page */}
          <Route
            path="/signin"
            element={
              isAuthenticated ? <Navigate to="/" replace /> : <SignInPage setIsAuthenticated={setIsAuthenticated} />
            }
          />

          {/* Protected routes for authenticated users */}
          {isAuthenticated ? (
            <>
              <Route path="/" element={<Dashboard />} />
              <Route path="/contacts" element={<ContactList />} />
              <Route path="/tasks" element={<TaskManager />} />
              <Route path="/settings" element={<Settings />} />
            </>
          ) : (
            <Route path="*" element={<Navigate to="/signin" replace />} />
          )}

          {/* Redirect undefined routes */}
          <Route path="*" element={<Navigate to="/signin" replace />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
