import React, { useState, useEffect } from 'react';
import './ContactList.css';
import EmailSummary from '../Email/EmailSummary';
import Modal from '../Modal'; // Modal component
import axios from 'axios';

const ContactList = () => {
  const [contacts, setContacts] = useState([]);
  const [selectedContact, setSelectedContact] = useState(null); // For editing
  const [isModalOpen, setIsModalOpen] = useState(false); // Modal state
  const [isAddingNew, setIsAddingNew] = useState(false); // Distinguish between add/edit mode
  const [isLoading, setIsLoading] = useState(true);
  const [activeMenu, setActiveMenu] = useState(null); // Tracks which contact's menu is open

  // Fetch contacts from the backend
  useEffect(() => {
    const fetchContacts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/contacts');
        setContacts(response.data);
      } catch (error) {
        console.error('Failed to fetch contacts:', error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchContacts();
  }, []);

  // Open the modal for adding or editing
  const handleOpenModal = (contact = null) => {
    setSelectedContact(contact);
    setIsAddingNew(!contact); // If no contact, it's an "Add" operation
    setIsModalOpen(true);
    setActiveMenu(null); // Close the dropdown menu
  };

  // Close the modal
  const handleCloseModal = () => {
    setSelectedContact(null);
    setIsModalOpen(false);
  };

  // Delete a contact
  const handleDeleteContact = async (id) => {
    if (window.confirm('Are you sure you want to delete this contact?')) {
      try {
        await axios.delete(`http://localhost:5000/api/contacts/${id}`);
        setContacts((prev) => prev.filter((contact) => contact._id !== id));
      } catch (error) {
        console.error('Failed to delete contact:', error);
      }
    }
    setActiveMenu(null); // Close the dropdown menu
  };

  // Handle form submission for add/edit
  const handleFormSubmit = async (formData) => {
    try {
      if (isAddingNew) {
        const response = await axios.post('http://localhost:5000/api/contacts', formData);
        setContacts((prev) => [...prev, response.data]);
      } else {
        const response = await axios.put(`http://localhost:5000/api/contacts/${selectedContact._id}`, formData);
        setContacts((prev) =>
          prev.map((contact) => (contact._id === response.data._id ? response.data : contact))
        );
      }
      handleCloseModal();
    } catch (error) {
      console.error('Failed to save contact:', error);
    }
  };

  // Toggle the dropdown menu for a contact
  const toggleMenu = (contactId) => {
    setActiveMenu((prev) => (prev === contactId ? null : contactId));
  };

  return (
    <div className="contact-list">
      <h1>Contact List</h1>

      {/* Add Contact Button */}
      <button onClick={() => handleOpenModal()}>Add New Contact</button>

      {/* Contact List */}
      {isLoading ? (
        <p>Loading contacts...</p>
      ) : contacts.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Phone</th>
              <th>Email</th>
              <th>Notes</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {contacts.map((contact) => (
              <tr key={contact._id}>
                <td>{contact.name}</td>
                <td>{contact.phone}</td>
                <td>{contact.email}</td>
                <td>{contact.notes}</td>
                <td>
                  {/* Three Dots Menu */}
                  <div className="dropdown">
                    <button className="dots" onClick={() => toggleMenu(contact._id)}>
                      ...
                    </button>
                    {activeMenu === contact._id && (
                      <div className="dropdown-menu">
                        <button onClick={() => handleOpenModal(contact)}>Edit</button>
                        <button onClick={() => handleDeleteContact(contact._id)}>Delete</button>
                      </div>
                    )}
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No contacts found.</p>
      )}

      {/* Add/Edit Contact Modal */}
      {isModalOpen && (
        <Modal onClose={handleCloseModal}>
          <h2>{isAddingNew ? 'Add New Contact' : `Edit Contact: ${selectedContact?.name}`}</h2>
          <ContactForm
            contact={selectedContact}
            onSubmit={handleFormSubmit}
            onCancel={handleCloseModal}
          />
        </Modal>
      )}
    </div>
  );
};

export default ContactList;
