import React, { useState } from 'react';
import TaskList from './TaskList';
import AddTaskModal from './AddTaskModal';
import './TaskManager.css';

const TaskManager = () => {
  const [tasks, setTasks] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editTaskIndex, setEditTaskIndex] = useState(null);

  const addOrUpdateTask = (task) => {
    if (editTaskIndex !== null) {
      setTasks((prevTasks) =>
        prevTasks.map((t, index) => (index === editTaskIndex ? task : t))
      );
      setEditTaskIndex(null);
    } else {
      setTasks((prevTasks) => [...prevTasks, task]);
    }
    setIsModalOpen(false);
  };

  const openModal = (index = null) => {
    setEditTaskIndex(index);
    setIsModalOpen(true);
  };

  const deleteTask = (index) => {
    setTasks((prevTasks) => prevTasks.filter((_, i) => i !== index));
  };

  return (
    <div className="task-manager">
      <h1>Task Manager</h1>
      <button className="add-task-button" onClick={() => openModal()}>
        Add Task
      </button>
      <TaskList tasks={tasks} onEdit={openModal} onDelete={deleteTask} />
      {isModalOpen && (
        <AddTaskModal
          onAddTask={addOrUpdateTask}
          onClose={() => setIsModalOpen(false)}
          task={editTaskIndex !== null ? tasks[editTaskIndex] : null}
        />
      )}
    </div>
  );
};

export default TaskManager;
