import React, { useEffect, useState } from 'react';
import axios from 'axios';

const EmailList = ({ clientId, onEmailClick }) => {
  const [emails, setEmails] = useState([]);

  useEffect(() => {
    const fetchEmails = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/emails/client/${clientId}`);
        setEmails(response.data);
      } catch (error) {
        console.error('Failed to fetch emails:', error);
      }
    };

    fetchEmails();
  }, [clientId]);

  return (
    <div>
      <h2>Email History</h2>
      <ul>
        {emails.map((email) => (
          <li key={email._id} onClick={() => onEmailClick(email._id)}>
            <strong>{email.subject}</strong> - {email.status} - {new Date(email.dateSent).toLocaleString()}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EmailList;
