import React, { useState, useEffect } from 'react';
import './ContactList.css';
import axios from 'axios';

const ContactList = () => {
  const [contacts, setContacts] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [currentContact, setCurrentContact] = useState({
    name: '',
    phone: '',
    email: '',
    notes: '',
    tasks: [],  // Add task details here
  });

  const [isTaskPopupOpen, setIsTaskPopupOpen] = useState(false);  // Manage task popup visibility

  useEffect(() => {
    const fetchContacts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/contacts');
        setContacts(response.data);
      } catch (error) {
        console.error('Error fetching contacts:', error);
      }
    };

    fetchContacts();
  }, []);

  const handleAddContact = () => {
    setCurrentContact({ name: '', phone: '', email: '', notes: '', tasks: [] });
    setIsEditing(true);
  };

  const handleEditContact = (contact) => {
    setCurrentContact(contact);
    setIsEditing(true);
  };

  const handleDeleteContact = async (id) => {
    if (window.confirm('Are you sure you want to delete this contact?')) {
      try {
        await axios.delete(`http://localhost:5000/api/contacts/${id}`);
        setContacts((prev) => prev.filter((contact) => contact._id !== id));
      } catch (error) {
        console.error('Error deleting contact:', error);
      }
    }
  };

  const handleAddTask = () => {
    setIsTaskPopupOpen(true); // Open task popup
  };

  const handleSaveTask = async (newTask) => {
    // Save new task logic
    try {
      const response = await axios.post(`http://localhost:5000/api/contacts/${currentContact._id}/tasks`, newTask);
      setCurrentContact((prevContact) => ({
        ...prevContact,
        tasks: [...prevContact.tasks, response.data], // Add task to the contact's tasks array
      }));
      setIsTaskPopupOpen(false); // Close task popup
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };

  return (
    <div className="contact-list">
      <h1>Contact List</h1>
      <button onClick={handleAddContact} className="add-contact-button">Add Contact</button>

      {isEditing ? (
        <div className="contact-form">
          <h2>{currentContact._id ? 'Edit Contact' : 'Add Contact'}</h2>
          <form>
            {/* Display Contact Form Details */}
            <label>Name: <input type="text" value={currentContact.name} /></label>
            <label>Phone: <input type="text" value={currentContact.phone} /></label>
            <label>Email: <input type="email" value={currentContact.email} /></label>
            <label>Notes: <textarea value={currentContact.notes} /></label>
            <button type="button" onClick={() => setIsEditing(false)}>Save</button>
            <button type="button" onClick={() => setIsEditing(false)}>Cancel</button>
          </form>
          <div>
            {/* Show Task List for Current Contact */}
            <h3>Tasks:</h3>
            <ul>
              {currentContact.tasks.map((task) => (
                <li key={task._id}>
                  <p>{task.title} - {task.status}</p>
                </li>
              ))}
            </ul>
            <button onClick={handleAddTask} className="add-task-button">Add Task</button>
          </div>
        </div>
      ) : (
        <div className="contact-table">
          {/* Display contacts in table */}
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Phone</th>
                <th>Email</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {contacts.map((contact) => (
                <tr key={contact._id}>
                  <td onClick={() => handleEditContact(contact)}>{contact.name}</td>
                  <td onClick={() => handleEditContact(contact)}>{contact.phone}</td>
                  <td onClick={() => handleEditContact(contact)}>{contact.email}</td>
                  <td>
                    <button onClick={() => handleDeleteContact(contact._id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* Task Popup */}
      {isTaskPopupOpen && (
        <div className="popup">
          <div className="popup-content">
            <button className="close-button" onClick={() => setIsTaskPopupOpen(false)}>&times;</button>
            <h2>Add Task</h2>
            <TaskForm onSave={handleSaveTask} />
          </div>
        </div>
      )}
    </div>
  );
};

export default ContactList;
