import React, { useState, useEffect } from 'react';
import './ContactList.css';
import axios from 'axios';

const ContactList = () => {
  const [contacts, setContacts] = useState([]);
  const [selectedContact, setSelectedContact] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [showPopup, setShowPopup] = useState(false);
  const [contactForm, setContactForm] = useState({
    name: '',
    phone: '',
    email: '',
    notes: '',
  });
  const [taskForm, setTaskForm] = useState({
    title: '',
    dueDate: '',
    description: '',
    status: '',
  });
  const [isEditingTask, setIsEditingTask] = useState(null);

  useEffect(() => {
    const fetchContacts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/contacts');
        setContacts(response.data);
      } catch (error) {
        console.error('Error fetching contacts:', error);
      }
    };

    fetchContacts();
  }, []);

  const fetchTasksForContact = async (contactId) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/contacts/${contactId}/tasks`);
      setTasks(response.data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  const handleViewContact = (contact) => {
    setSelectedContact(contact);
    setContactForm(contact);
    fetchTasksForContact(contact._id);
    setShowPopup(true);
  };

  const handleClosePopup = () => {
    setShowPopup(false);
    setSelectedContact(null);
    setTasks([]);
    setContactForm({
      name: '',
      phone: '',
      email: '',
      notes: '',
    });
    setTaskForm({
      title: '',
      dueDate: '',
      description: '',
      status: '',
    });
    setIsEditingTask(null);
  };

  const handleContactFormChange = (e) => {
    const { name, value } = e.target;
    setContactForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleTaskFormChange = (e) => {
    const { name, value } = e.target;
    setTaskForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleSaveContact = async () => {
    try {
      if (selectedContact) {
        const response = await axios.put(
          `http://localhost:5000/api/contacts/${selectedContact._id}`,
          contactForm
        );
        setContacts((prev) =>
          prev.map((contact) =>
            contact._id === selectedContact._id ? response.data : contact
          )
        );
      } else {
        const response = await axios.post('http://localhost:5000/api/contacts', contactForm);
        setContacts((prev) => [...prev, response.data]);
      }
      handleClosePopup();
    } catch (error) {
      console.error('Error saving contact:', error);
    }
  };

  const handleSaveTask = async () => {
    try {
      if (isEditingTask) {
        const response = await axios.put(
          `http://localhost:5000/api/tasks/${isEditingTask}`,
          taskForm
        );
        setTasks((prev) =>
          prev.map((task) => (task._id === isEditingTask ? response.data : task))
        );
      } else {
        const response = await axios.post(
          `http://localhost:5000/api/contacts/${selectedContact._id}/tasks`,
          taskForm
        );
        setTasks((prev) => [...prev, response.data]);
        setContacts((prev) =>
          prev.map((contact) =>
            contact._id === selectedContact._id
              ? { ...contact, tasks: [...(contact.tasks || []), response.data] }
              : contact
          )
        );
      }
      setIsEditingTask(null);
      setTaskForm({
        title: '',
        dueDate: '',
        description: '',
        status: '',
      });
    } catch (error) {
      console.error('Error saving task:', error);
    }
  };

  const handleEditTask = (task) => {
    setIsEditingTask(task._id);
    setTaskForm(task);
  };

  return (
    <div className="contact-list">
      <h1>Contact List</h1>
      <button
        onClick={() => {
          setSelectedContact(null);
          setContactForm({ name: '', phone: '', email: '', notes: '' });
          setShowPopup(true);
        }}
      >
        Add Contact
      </button>

      <div className="contact-table">
        {contacts.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Phone</th>
                <th>Email</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {contacts.map((contact) => (
                <tr key={contact._id}>
                  <td>{contact.name}</td>
                  <td>{contact.phone}</td>
                  <td>{contact.email}</td>
                  <td>
                    <button onClick={() => handleViewContact(contact)}>View/Edit</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No contacts available.</p>
        )}
      </div>

      {showPopup && (
        <div className="popup">
          <div className="popup-content">
            <button className="close-button" onClick={handleClosePopup}>
              &times;
            </button>
            <h2>{selectedContact ? 'Edit Contact' : 'Add Contact'}</h2>
            <form>
              <label>
                Name:
                <input
                  type="text"
                  name="name"
                  value={contactForm.name}
                  onChange={handleContactFormChange}
                  required
                />
              </label>
              <label>
                Phone:
                <input
                  type="text"
                  name="phone"
                  value={contactForm.phone}
                  onChange={handleContactFormChange}
                  required
                />
              </label>
              <label>
                Email:
                <input
                  type="email"
                  name="email"
                  value={contactForm.email}
                  onChange={handleContactFormChange}
                  required
                />
              </label>
              <label>
                Notes:
                <textarea
                  name="notes"
                  value={contactForm.notes}
                  onChange={handleContactFormChange}
                />
              </label>
              <button type="button" onClick={handleSaveContact}>
                Save Contact
              </button>
            </form>

            {selectedContact && (
              <>
                <h2>Tasks</h2>
                {tasks.length > 0 ? (
                  tasks.map((task) => (
                    <div key={task._id} className="task-item">
                      <p><strong>{task.title}</strong></p>
                      <button onClick={() => handleEditTask(task)}>Edit</button>
                    </div>
                  ))
                ) : (
                  <p>No tasks available.</p>
                )}

                <h3>{isEditingTask ? 'Edit Task' : 'Add Task'}</h3>
                <form>
                  <label>
                    Title:
                    <input
                      type="text"
                      name="title"
                      value={taskForm.title}
                      onChange={handleTaskFormChange}
                      required
                    />
                  </label>
                  <label>
                    Due Date:
                    <input
                      type="date"
                      name="dueDate"
                      value={taskForm.dueDate}
                      onChange={handleTaskFormChange}
                      required
                    />
                  </label>
                  <label>
                    Description:
                    <textarea
                      name="description"
                      value={taskForm.description}
                      onChange={handleTaskFormChange}
                    />
                  </label>
                  <label>
                    Status:
                    <select
                      name="status"
                      value={taskForm.status}
                      onChange={handleTaskFormChange}
                      required
                    >
                      <option value="">Select Status</option>
                      <option value="Pending">Pending</option>
                      <option value="In Progress">In Progress</option>
                      <option value="Completed">Completed</option>
                    </select>
                  </label>
                  <button type="button" onClick={handleSaveTask}>
                    {isEditingTask ? 'Update Task' : 'Add Task'}
                  </button>
                </form>
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default ContactList;
