import React, { useState } from 'react';
import './SignIn.css'; // For styles
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for redirection

const SignIn = () => {
  const [userId, setUserId] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState('');

  const navigate = useNavigate(); // Create a navigate function

  // Default credentials for sign-in validation
  const defaultCredentials = {
    userId: 'admin', // Default user ID
    password: '123', // Default password
  };

  // Handle form submission for sign-in
  const handleSignIn = (e) => {
    e.preventDefault();

    // Validate user credentials
    if (userId === defaultCredentials.userId && password === defaultCredentials.password) {
      setError(''); // Clear previous errors if any
      localStorage.setItem('isAuthenticated', JSON.stringify(true)); // Save auth state
      navigate('/dashboard'); // Redirect to the dashboard
    } else {
      setError('Invalid credentials, please try again.'); // Show error message
    }
  };

  return (
    <div className="signin-container">
      <div className="left-panel">
        <motion.div
          className="welcome-animation"
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 1 }}
        >
          <h1>Manage Your Business, Effortlessly</h1>
          <p>Sign in to your dashboard and stay on top of everything.</p>
        </motion.div>
      </div>

      <div className="right-panel">
        <motion.form
          className="signin-form"
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
          onSubmit={handleSignIn}
        >
          <h2>Welcome Back!</h2>

          {error && <div className="error-message">{error}</div>} {/* Display error message */}

          <div className="input-group">
            <label htmlFor="userId">User ID</label>
            <input
              type="text"
              id="userId"
              value={userId}
              onChange={(e) => setUserId(e.target.value)}
              placeholder="Enter your user ID"
              required
            />
          </div>

          <div className="input-group">
            <label htmlFor="password">Password</label>
            <div className="password-wrapper">
              <input
                type={showPassword ? 'text' : 'password'}
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter your password"
                required
              />
              <button
                type="button"
                className="toggle-password"
                onClick={() => setShowPassword(!showPassword)}
              >
                {showPassword ? '🙈' : '👁️'}
              </button>
            </div>
          </div>

          <div className="options">
            <label>
              <input type="checkbox" /> Remember Me
            </label>
            <a href="/forgot-password">Forgot Password?</a>
          </div>

          <button type="submit" className="signin-button">
            Sign In
          </button>
        </motion.form>
      </div>
    </div>
  );
};

export default SignIn;
