import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TaskList from './TaskList';
import AddTaskModal from './AddTaskModal';
import './TaskManager.css';

const TaskManager = () => {
  const [tasks, setTasks] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editTask, setEditTask] = useState(null);

  // Fetch tasks from API on component mount
  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/tasks');
        setTasks(response.data);
      } catch (error) {
        console.error('Failed to fetch tasks:', error);
      }
    };
    fetchTasks();
  }, []);

  // Add or update task
  const addOrUpdateTask = async (task) => {
    try {
      if (editTask) {
        // Update task
        const updatedTask = await axios.put(
          `http://localhost:5000/api/tasks/${editTask._id}`,
          task
        );
        setTasks((prevTasks) =>
          prevTasks.map((t) => (t._id === editTask._id ? updatedTask.data : t))
        );
        setEditTask(null);
      } else {
        // Add new task
        const addedTask = await axios.post('http://localhost:5000/api/tasks', task);
        setTasks((prevTasks) => [...prevTasks, addedTask.data]);
      }
      setIsModalOpen(false);
    } catch (error) {
      console.error('Failed to save task:', error);
    }
  };

  // Open modal to edit a task or add a new task
  const openModal = (task = null) => {
    console.log('openModal called with task:', task);
    setEditTask(task);
    setIsModalOpen(true);
    console.log('isModalOpen set to:', true);
  };

  // Delete task
  const deleteTask = async (taskId) => {
    try {
      await axios.delete(`http://localhost:5000/api/tasks/${taskId}`);
      setTasks((prevTasks) => prevTasks.filter((task) => task._id !== taskId));
    } catch (error) {
      console.error('Failed to delete task:', error);
    }
  };

  return (
    <div className="task-manager">
      <h1>Task Manager</h1>
      <button
        className="add-task-button"
        onClick={() => {
          console.log('Add Task button clicked');
          openModal();
        }}
      >
        Add Task
      </button>
      <TaskList tasks={tasks} onEdit={openModal} onDelete={deleteTask} />
      {isModalOpen && (
        <AddTaskModal
          onAddTask={addOrUpdateTask}
          onClose={() => {
            console.log('Modal closed');
            setIsModalOpen(false);
          }}
          task={editTask}
        />
      )}
    </div>
  );
};

export default TaskManager;
