import React, { useState, useEffect } from 'react';
import './ContactList.css';
import axios from 'axios';

const ContactList = () => {
  const [contacts, setContacts] = useState([]);
  const [filteredContacts, setFilteredContacts] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [currentContact, setCurrentContact] = useState({
    name: '',
    email: '',
    phone: '',
    company: '',
    position: '',
    notes: '',
    type: 'lead',
    leadScore: '',
    lastInteraction: '',
  });
  const [sortOption, setSortOption] = useState('name');
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const fetchContacts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/contacts');
        setContacts(response.data);
        setFilteredContacts(response.data);
      } catch (error) {
        console.error('Failed to fetch contacts:', error);
      }
    };

    fetchContacts();
  }, []);

  const sortContacts = (contacts, criteria) => {
    const sorted = [...contacts];
    switch (criteria) {
      case 'name':
        return sorted.sort((a, b) => a.name.localeCompare(b.name));
      case 'email':
        return sorted.sort((a, b) => a.email.localeCompare(b.email));
      case 'type':
        return sorted.sort((a, b) => a.type.localeCompare(b.type));
      case 'company':
        return sorted.sort((a, b) => a.company.localeCompare(b.company));
      default:
        return sorted;
    }
  };

  useEffect(() => {
    setFilteredContacts((prevContacts) => sortContacts(prevContacts, sortOption));
  }, [sortOption]);

  useEffect(() => {
    const lowercasedSearchTerm = searchTerm.toLowerCase();
    const filtered = contacts.filter(
      (contact) =>
        contact.name.toLowerCase().includes(lowercasedSearchTerm) ||
        contact.email.toLowerCase().includes(lowercasedSearchTerm) ||
        contact.company.toLowerCase().includes(lowercasedSearchTerm)
    );
    setFilteredContacts(filtered);
  }, [searchTerm, contacts]);

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setCurrentContact((prev) => ({ ...prev, [name]: value }));
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      if (currentContact._id) {
        const response = await axios.put(
          `http://localhost:5000/api/contacts/${currentContact._id}`,
          currentContact
        );
        setContacts((prev) =>
          prev.map((contact) => (contact._id === response.data._id ? response.data : contact))
        );
        setFilteredContacts((prev) =>
          prev.map((contact) => (contact._id === response.data._id ? response.data : contact))
        );
      } else {
        const response = await axios.post('http://localhost:5000/api/contacts', currentContact);
        setContacts((prev) => [...prev, response.data]);
        setFilteredContacts((prev) => [...prev, response.data]);
      }
      setIsEditing(false);
      setCurrentContact({
        name: '',
        email: '',
        phone: '',
        company: '',
        position: '',
        notes: '',
        type: 'lead',
        leadScore: '',
        lastInteraction: '',
      });
    } catch (error) {
      console.error('Failed to save contact:', error);
    }
  };

  const handleAddContact = () => {
    setCurrentContact({
      name: '',
      email: '',
      phone: '',
      company: '',
      position: '',
      notes: '',
      type: 'lead',
      leadScore: '',
      lastInteraction: '',
    });
    setIsEditing(true);
  };

  const handleContactClick = (contact) => {
    setCurrentContact(contact);
    setIsEditing(true);
  };

  const handleDeleteContact = async (id) => {
    if (window.confirm('Are you sure you want to delete this contact?')) {
      try {
        await axios.delete(`http://localhost:5000/api/contacts/${id}`);
        setContacts((prev) => prev.filter((contact) => contact._id !== id));
        setFilteredContacts((prev) => prev.filter((contact) => contact._id !== id));
      } catch (error) {
        console.error('Failed to delete contact:', error);
      }
    }
  };

  return (
    <div className="contact-list">
      <h1>Contact List</h1>
      <button onClick={handleAddContact}>Add Contact</button>
      
      {/* Search Bar */}
      <input
        type="text"
        id="search"
        placeholder="Search by name, email, or company"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      {/* Sorting Options */}
      <div>
        <label htmlFor="sort">Sort by:</label>
        <select
          id="sort"
          value={sortOption}
          onChange={(e) => setSortOption(e.target.value)}
        >
          <option value="name">Name</option>
          <option value="email">Email</option>
          <option value="type">Type</option>
          <option value="company">Company</option>
        </select>
      </div>

      {isEditing ? (
        <div className="contact-form">
          <h2>{currentContact._id ? 'Edit Contact' : 'Add Contact'}</h2>
          <form onSubmit={handleFormSubmit}>
            <label>Name:</label>
            <input
              type="text"
              name="name"
              value={currentContact.name}
              onChange={handleFormChange}
              required
            />
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={currentContact.email}
              onChange={handleFormChange}
              required
            />
            <label>Phone:</label>
            <input
              type="text"
              name="phone"
              value={currentContact.phone}
              onChange={handleFormChange}
            />
            <label>Company:</label>
            <input
              type="text"
              name="company"
              value={currentContact.company}
              onChange={handleFormChange}
            />
            <label>Type:</label>
            <select
              name="type"
              value={currentContact.type}
              onChange={handleFormChange}
            >
              <option value="lead">Lead</option>
              <option value="contact">Contact</option>
            </select>
            <label>Position:</label>
            <input
              type="text"
              name="position"
              value={currentContact.position}
              onChange={handleFormChange}
            />
            <label>Notes:</label>
            <textarea
              name="notes"
              value={currentContact.notes}
              onChange={handleFormChange}
            />
            <label>Lead Score:</label>
            <input
              type="number"
              name="leadScore"
              value={currentContact.leadScore}
              onChange={handleFormChange}
              min="0"
              max="100"
            />
            <label>Last Interaction:</label>
            <input
              type="date"
              name="lastInteraction"
              value={currentContact.lastInteraction?.slice(0, 10)}
              onChange={handleFormChange}
            />
            <button type="submit">Save</button>
            <button type="button" onClick={() => setIsEditing(false)}>
              Cancel
            </button>
          </form>
        </div>
      ) : (
        <>
          {filteredContacts.length > 0 ? (
            <table>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Email</th>
                  <th>Phone</th>
                  <th>Company</th>
                  <th>Type</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredContacts.map((contact) => (
                  <tr key={contact._id}>
                    <td onClick={() => handleContactClick(contact)}>{contact.name}</td>
                    <td onClick={() => handleContactClick(contact)}>{contact.email}</td>
                    <td onClick={() => handleContactClick(contact)}>{contact.phone}</td>
                    <td onClick={() => handleContactClick(contact)}>{contact.company}</td>
                    <td onClick={() => handleContactClick(contact)}>{contact.type}</td>
                    <td>
                      <button onClick={() => handleDeleteContact(contact._id)}>Delete</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p>No contacts found.</p>
          )}
        </>
      )}
    </div>
  );
};

export default ContactList;
