import React, { useState, useEffect } from 'react';
import './ContactList.css';
import axios from 'axios';

const ContactList = () => {
  const [contacts, setContacts] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [currentContact, setCurrentContact] = useState({
    name: '',
    phone: '',
    email: '',
    notes: '',
  });
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');
  const [selectedContact, setSelectedContact] = useState(null);

  useEffect(() => {
    fetchContacts();
  }, []);

  const fetchContacts = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/contacts');
      setContacts(response.data);
    } catch (error) {
      console.error('Error fetching contacts:', error);
    }
  };

  const fetchTasks = async (contactId) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/contacts/${contactId}/tasks`);
      setTasks(response.data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCurrentContact((prev) => ({ ...prev, [name]: value }));
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      if (currentContact._id) {
        const response = await axios.put(
          `http://localhost:5000/api/contacts/${currentContact._id}`,
          currentContact
        );
        setContacts((prev) =>
          prev.map((contact) => (contact._id === response.data._id ? response.data : contact))
        );
      } else {
        const response = await axios.post('http://localhost:5000/api/contacts', currentContact);
        setContacts((prev) => [...prev, response.data]);
      }
      resetForm();
    } catch (error) {
      console.error('Error saving contact:', error);
    }
  };

  const handleAddContact = () => {
    setCurrentContact({ name: '', phone: '', email: '', notes: '' });
    setIsEditing(true);
  };

  const handleEditContact = (contact) => {
    setCurrentContact(contact);
    setIsEditing(true);
  };

  const handleDeleteContact = async (id) => {
    if (window.confirm('Are you sure you want to delete this contact?')) {
      try {
        await axios.delete(`http://localhost:5000/api/contacts/${id}`);
        setContacts((prev) => prev.filter((contact) => contact._id !== id));
      } catch (error) {
        console.error('Error deleting contact:', error);
      }
    }
  };

  const resetForm = () => {
    setIsEditing(false);
    setCurrentContact({ name: '', phone: '', email: '', notes: '' });
  };

  const handleViewTasks = async (contact) => {
    setSelectedContact(contact);
    fetchTasks(contact._id);
  };

  const handleAddTask = async () => {
    if (!newTask.trim()) return;

    try {
      const response = await axios.post(
        `http://localhost:5000/api/contacts/${selectedContact._id}/tasks`,
        { description: newTask }
      );
      setTasks((prev) => [...prev, response.data]);
      setNewTask('');
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };

  return (
    <div className="contact-list">
      <h1>Contact List</h1>
      <button onClick={handleAddContact} className="add-contact-button">
        Add Contact
      </button>

      {isEditing ? (
        <div className="contact-form">
          <h2>{currentContact._id ? 'Edit Contact' : 'Add Contact'}</h2>
          <form onSubmit={handleFormSubmit}>
            <label>
              Name:
              <input
                type="text"
                name="name"
                value={currentContact.name}
                onChange={handleInputChange}
                required
              />
            </label>
            <label>
              Phone:
              <input
                type="text"
                name="phone"
                value={currentContact.phone}
                onChange={handleInputChange}
                required
              />
            </label>
            <label>
              Email:
              <input
                type="email"
                name="email"
                value={currentContact.email}
                onChange={handleInputChange}
                required
              />
            </label>
            <label>
              Notes:
              <textarea
                name="notes"
                value={currentContact.notes}
                onChange={handleInputChange}
              />
            </label>
            <div className="form-actions">
              <button type="submit">Save</button>
              <button type="button" onClick={resetForm}>
                Cancel
              </button>
            </div>
          </form>
        </div>
      ) : (
        <div className="contact-table">
          {contacts.length > 0 ? (
            <table>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Phone</th>
                  <th>Email</th>
                  <th>Notes</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {contacts.map((contact) => (
                  <tr key={contact._id}>
                    <td onClick={() => handleEditContact(contact)}>{contact.name}</td>
                    <td>{contact.phone}</td>
                    <td>{contact.email}</td>
                    <td>{contact.notes}</td>
                    <td>
                      <button onClick={() => handleDeleteContact(contact._id)}>Delete</button>
                      <button onClick={() => handleViewTasks(contact)}>View Tasks</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p>No contacts available.</p>
          )}
        </div>
      )}

      {selectedContact && (
        <div className="tasks-section">
          <h2>Tasks for {selectedContact.name}</h2>
          <ul>
            {tasks.map((task) => (
              <li key={task._id}>{task.description}</li>
            ))}
          </ul>
          <input
            type="text"
            value={newTask}
            onChange={(e) => setNewTask(e.target.value)}
            placeholder="New Task"
          />
          <button onClick={handleAddTask}>Add Task</button>
        </div>
      )}
    </div>
  );
};

export default ContactList;
