// Import necessary dependencies
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './SignIn.css'; // Assuming you will style the page separately

const SignInPage = () => {
  const [userId, setUserId] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSignIn = (e) => {
    e.preventDefault();
    setError('');

    // Placeholder logic for login validation
    if (userId === 'admin' && password === 'password123') {
      console.log('Sign-in successful');
      navigate('/dashboard'); // Navigate to the dashboard on successful login
    } else {
      setError('Invalid User ID or Password. Please try again.');
    }
  };

  return (
    <div className="signin-container">
      <div className="signin-box">
        <h2>Sign In to CRM</h2>

        {error && <p className="error-message">{error}</p>}

        <form className="signin-form" onSubmit={handleSignIn}>
          <div className="form-group">
            <label htmlFor="userId">User ID:</label>
            <input
              type="text"
              id="userId"
              value={userId}
              onChange={(e) => setUserId(e.target.value)}
              placeholder="Enter your User ID"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your Password"
              required
            />
          </div>

          <button type="submit" className="signin-button">Sign In</button>
        </form>

        <p className="forgot-password">
          Forgot your password? <a href="/reset-password">Reset it here</a>
        </p>
      </div>
    </div>
  );
};

export default SignInPage;
