import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EmailDetails = ({ emailId }) => {
  const [emailThread, setEmailThread] = useState([]);

  useEffect(() => {
    const fetchEmailThread = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/emails/thread/${emailId}`);
        setEmailThread(response.data);
      } catch (error) {
        console.error('Error fetching email thread:', error);
      }
    };

    fetchEmailThread();
  }, [emailId]);

  return (
    <div className="email-details">
      <h3>Email Conversation</h3>
      {emailThread.map((email) => (
        <div key={email._id} className="email-message">
          <p><strong>From:</strong> {email.to}</p>
          <p><strong>Subject:</strong> {email.subject}</p>
          <p><strong>Date Sent:</strong> {new Date(email.dateSent).toLocaleString()}</p>
          <p>{email.body}</p>
          {email.parentEmailId && <p><strong>In reply to:</strong> {email.parentEmailId}</p>}
        </div>
      ))}
    </div>
  );
};

export default EmailDetails;
