import React, { useState, useEffect } from 'react';
import './ContactList.css';
import axios from 'axios';

const ContactList = () => {
  const [contacts, setContacts] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [isViewing, setIsViewing] = useState(false);
  const [currentContact, setCurrentContact] = useState({
    name: '',
    phone: '',
    email: '',
    notes: '',
    tasks: [] // Array to hold tasks
  });
  const [newTask, setNewTask] = useState({
    title: '',
    dueDate: '',
    description: '',
    status: 'Pending',
  });

  useEffect(() => {
    const fetchContacts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/contacts');
        setContacts(response.data);
      } catch (error) {
        console.error('Failed to fetch contacts:', error);
      }
    };

    fetchContacts();
  }, []);

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setCurrentContact((prev) => ({ ...prev, [name]: value }));
  };

  const handleTaskChange = (e) => {
    const { name, value } = e.target;
    setNewTask((prev) => ({ ...prev, [name]: value }));
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      let response;
      if (currentContact._id) {
        // Edit contact
        response = await axios.put(
          `http://localhost:5000/api/contacts/${currentContact._id}`,
          currentContact
        );
        setContacts((prev) =>
          prev.map((contact) => (contact._id === response.data._id ? response.data : contact))
        );
      } else {
        // Add new contact
        response = await axios.post('http://localhost:5000/api/contacts', currentContact);
        setContacts((prev) => [...prev, response.data]);
      }
      setIsEditing(false);
      setCurrentContact({ name: '', phone: '', email: '', notes: '', tasks: [] });
    } catch (error) {
      console.error('Failed to save contact:', error);
    }
  };

  const handleAddContact = () => {
    setCurrentContact({ name: '', phone: '', email: '', notes: '', tasks: [] });
    setIsEditing(true);
  };

  const handleContactClick = (contact) => {
    setCurrentContact(contact);
    setIsViewing(true);
  };

  const handleDeleteContact = async (id) => {
    if (window.confirm('Are you sure you want to delete this contact?')) {
      try {
        await axios.delete(`http://localhost:5000/api/contacts/${id}`);
        setContacts((prev) => prev.filter((contact) => contact._id !== id));
      } catch (error) {
        console.error('Failed to delete contact:', error);
      }
    }
  };

  const handleAddTask = async () => {
    if (!newTask.title || !newTask.dueDate || !newTask.status) {
      alert('Please fill out all fields for the task.');
      return;
    }

    try {
      // Add task to current contact
      const updatedContact = { ...currentContact, tasks: [...currentContact.tasks, newTask] };
      await axios.put(`http://localhost:5000/api/contacts/${currentContact._id}`, updatedContact);

      // Update the contact list
      setContacts((prev) =>
        prev.map((contact) =>
          contact._id === updatedContact._id ? updatedContact : contact
        )
      );
      setNewTask({ title: '', dueDate: '', description: '', status: 'Pending' });
      setIsViewing(false); // Close the view modal after saving
    } catch (error) {
      console.error('Failed to add task:', error);
    }
  };

  return (
    <div className="contact-list">
      <h1>Contact List</h1>
      <button onClick={handleAddContact} className="add-contact-button">Add Contact</button>

      {/* Contact Form (Add/Edit) */}
      {isEditing && (
        <div className="contact-form">
          <h2>{currentContact._id ? 'Edit Contact' : 'Add Contact'}</h2>
          <form onSubmit={handleFormSubmit}>
            <label>
              Name:
              <input
                type="text"
                name="name"
                value={currentContact.name}
                onChange={handleFormChange}
                required
              />
            </label>
            <label>
              Phone:
              <input
                type="text"
                name="phone"
                value={currentContact.phone}
                onChange={handleFormChange}
                required
              />
            </label>
            <label>
              Email:
              <input
                type="email"
                name="email"
                value={currentContact.email}
                onChange={handleFormChange}
                required
              />
            </label>
            <label>
              Notes:
              <textarea
                name="notes"
                value={currentContact.notes}
                onChange={handleFormChange}
              />
            </label>
            <div className="form-actions">
              <button type="submit">Save</button>
              <button type="button" onClick={() => setIsEditing(false)}>
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Contact List Table */}
      {!isEditing && (
        <div className="contact-table">
          {contacts.length > 0 ? (
            <table>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Phone</th>
                  <th>Email</th>
                  <th>Notes</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {contacts.map((contact) => (
                  <tr key={contact._id}>
                    <td onClick={() => handleContactClick(contact)}>{contact.name}</td>
                    <td>{contact.phone}</td>
                    <td>{contact.email}</td>
                    <td>{contact.notes}</td>
                    <td>
                      <button onClick={() => handleDeleteContact(contact._id)}>Delete</button>
                      <button onClick={() => handleContactClick(contact)}>View</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p>No contacts found.</p>
          )}
        </div>
      )}

      {/* View Contact Modal */}
      {isViewing && (
        <div className="view-contact-modal">
          <h2>{currentContact.name}</h2>
          <p><strong>Phone:</strong> {currentContact.phone}</p>
          <p><strong>Email:</strong> {currentContact.email}</p>
          <p><strong>Notes:</strong> {currentContact.notes}</p>

          <h3>Tasks</h3>
          {currentContact.tasks.length > 0 ? (
            <ul>
              {currentContact.tasks.map((task, index) => (
                <li key={index}>
                  <p><strong>{task.title}</strong></p>
                  <p><strong>Due Date:</strong> {task.dueDate}</p>
                  <p><strong>Status:</strong> {task.status}</p>
                  <p>{task.description}</p>
                </li>
              ))}
            </ul>
          ) : (
            <p>No tasks available for this contact.</p>
          )}

          <h3>Add Task</h3>
          <form onSubmit={(e) => e.preventDefault()}>
            <label>
              Task Title:
              <input
                type="text"
                name="title"
                value={newTask.title}
                onChange={handleTaskChange}
                required
              />
            </label>
            <label>
              Due Date:
              <input
                type="date"
                name="dueDate"
                value={newTask.dueDate}
                onChange={handleTaskChange}
                required
              />
            </label>
            <label>
              Status:
              <select
                name="status"
                value={newTask.status}
                onChange={handleTaskChange}
                required
              >
                <option value="Pending">Pending</option>
                <option value="In Progress">In Progress</option>
                <option value="Completed">Completed</option>
              </select>
            </label>
            <label>
              Description:
              <textarea
                name="description"
                value={newTask.description}
                onChange={handleTaskChange}
              />
            </label>
            <button onClick={handleAddTask}>Save Task</button>
            <button type="button" onClick={() => setIsViewing(false)}>Close</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default ContactList;
