import React, { useState, useEffect } from 'react';
import './ContactList.css';
import EmailSummary from '../Email/EmailSummary';
import EmailList from '../Email/EmailList';
import EmailDetails from '../Email/EmailDetails';
import EmailForm from '../Email/EmailForm';
import Modal from './Modal';
import ContactForm from './ContactForm'; // Importing the ContactForm component
import axios from 'axios';

const ContactList = () => {
  const [contacts, setContacts] = useState([]);
  const [selectedContact, setSelectedContact] = useState(null);
  const [selectedEmailId, setSelectedEmailId] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isAddingNew, setIsAddingNew] = useState(false);
  const [activeMenu, setActiveMenu] = useState(null);
  const [emails, setEmails] = useState([]);

  useEffect(() => {
    const fetchContacts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/contacts');
        setContacts(response.data);
      } catch (error) {
        console.error('Failed to fetch contacts:', error);
      }
    };

    fetchContacts();
  }, []);

  const handleOpenModal = (contact = null) => {
    setSelectedContact(contact);
    setIsAddingNew(!contact);
    setIsModalOpen(true);
    setActiveMenu(null);
  };

  const handleCloseModal = () => {
    setSelectedContact(null);
    setIsModalOpen(false);
  };

  const handleDeleteContact = async (id) => {
    if (window.confirm('Are you sure you want to delete this contact?')) {
      try {
        await axios.delete(`http://localhost:5000/api/contacts/${id}`);
        setContacts((prev) => prev.filter((contact) => contact._id !== id));
      } catch (error) {
        console.error('Failed to delete contact:', error);
      }
    }
    setActiveMenu(null);
  };

  const handleFormSubmit = async (formData) => {
    try {
      if (isAddingNew) {
        const response = await axios.post('http://localhost:5000/api/contacts', formData);
        setContacts((prev) => [...prev, response.data]);
      } else {
        const response = await axios.put(
          `http://localhost:5000/api/contacts/${selectedContact._id}`,
          formData
        );
        setContacts((prev) =>
          prev.map((contact) => (contact._id === response.data._id ? response.data : contact))
        );
      }
      handleCloseModal();
    } catch (error) {
      console.error('Failed to save contact:', error);
    }
  };

  const toggleMenu = (contactId) => {
    setActiveMenu((prev) => (prev === contactId ? null : contactId));
  };

  const handleContactClick = async (contact) => {
    setSelectedContact(contact);
    try {
      const response = await axios.get(`http://localhost:5000/api/emails/client/${contact._id}`);
      setEmails(response.data);
    } catch (error) {
      console.error('Failed to fetch emails:', error);
    }
  };

  const handleEmailClick = (emailId) => {
    setSelectedEmailId(emailId);
  };

  return (
    <div className="contact-list">
      <h1>Contact List</h1>

      <EmailSummary emails={emails} />
      <button onClick={() => handleOpenModal()}>Add New Contact</button>

      {contacts.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Phone</th>
              <th>Email</th>
              <th>Notes</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {contacts.map((contact) => (
              <tr key={contact._id} onClick={() => handleContactClick(contact)}>
                <td>{contact.name}</td>
                <td>{contact.phone}</td>
                <td>{contact.email}</td>
                <td>{contact.notes}</td>
                <td>
                  <div className="dropdown">
                    <button
                      className="dots"
                      onClick={(e) => {
                        e.stopPropagation();
                        toggleMenu(contact._id);
                      }}
                    >
                      ...
                    </button>
                    {activeMenu === contact._id && (
                      <div className="dropdown-menu">
                        <button
                          onClick={(e) => {
                            e.stopPropagation();
                            handleOpenModal(contact);
                          }}
                        >
                          Edit
                        </button>
                        <button
                          onClick={(e) => {
                            e.stopPropagation();
                            handleDeleteContact(contact._id);
                          }}
                        >
                          Delete
                        </button>
                      </div>
                    )}
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No contacts found.</p>
      )}

      {isModalOpen && (
        <Modal onClose={handleCloseModal}>
          <h2>{isAddingNew ? 'Add New Contact' : `Edit Contact: ${selectedContact?.name}`}</h2>
          <ContactForm
            contact={selectedContact}
            onSubmit={handleFormSubmit}
            onCancel={handleCloseModal}
          />
        </Modal>
      )}

      {selectedContact && (
        <div className="email-section">
          <h2>Email Details for {selectedContact.name}</h2>
          {selectedEmailId ? (
            <EmailDetails emailId={selectedEmailId} />
          ) : (
            <>
              <EmailForm clientId={selectedContact._id} />
              <EmailList clientId={selectedContact._id} onEmailClick={handleEmailClick} />
            </>
          )}
        </div>
      )}
    </div>
  );
};

export default ContactList;
