import React, { useState, useEffect } from 'react';
import './ContactList.css';
import axios from 'axios';

const ContactList = () => {
  const [contacts, setContacts] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [currentContact, setCurrentContact] = useState({
    name: '',
    email: '',
    phone: '',
    company: '',
    position: '',
    notes: '',
    type: 'lead', // Default type
    tags: [],
    leadScore: 0,
    preferredContactMethod: 'Email', // Default preferred contact method
    status: 'New', // Default status
  });

  useEffect(() => {
    const fetchContacts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/contacts');
        setContacts(response.data);
      } catch (error) {
        console.error('Failed to fetch contacts:', error);
      }
    };

    fetchContacts();
  }, []);

  const handleFormChange = (e) => {
    const { name, value } = e.target;

    // Handle specific fields
    if (name === 'tags') {
      const tagsArray = value.split(',').map((tag) => tag.trim());
      setCurrentContact((prev) => ({ ...prev, tags: tagsArray }));
    } else if (name === 'leadScore') {
      setCurrentContact((prev) => ({ ...prev, leadScore: parseInt(value, 10) || 0 }));
    } else {
      setCurrentContact((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      if (currentContact._id) {
        // Edit contact
        const response = await axios.put(
          `http://localhost:5000/api/contacts/${currentContact._id}`,
          currentContact
        );
        setContacts((prev) =>
          prev.map((contact) => (contact._id === response.data._id ? response.data : contact))
        );
      } else {
        // Add new contact
        const response = await axios.post('http://localhost:5000/api/contacts', currentContact);
        setContacts((prev) => [...prev, response.data]);
      }
      setIsEditing(false);
      setCurrentContact({
        name: '',
        email: '',
        phone: '',
        company: '',
        position: '',
        notes: '',
        type: 'lead',
        tags: [],
        leadScore: 0,
        preferredContactMethod: 'Email',
        status: 'New',
      });
    } catch (error) {
      console.error('Failed to save contact:', error);
    }
  };

  const handleAddContact = () => {
    setCurrentContact({
      name: '',
      email: '',
      phone: '',
      company: '',
      position: '',
      notes: '',
      type: 'lead',
      tags: [],
      leadScore: 0,
      preferredContactMethod: 'Email',
      status: 'New',
    });
    setIsEditing(true);
  };

  const handleContactClick = (contact) => {
    setCurrentContact(contact);
    setIsEditing(true);
  };

  const handleDeleteContact = async (id) => {
    if (window.confirm('Are you sure you want to delete this contact?')) {
      try {
        await axios.delete(`http://localhost:5000/api/contacts/${id}`);
        setContacts((prev) => prev.filter((contact) => contact._id !== id));
      } catch (error) {
        console.error('Failed to delete contact:', error);
      }
    }
  };

  return (
    <div className="contact-list">
      <h1>Contact List</h1>
      <button onClick={handleAddContact} className="add-contact-button">Add Contact</button>

      {isEditing ? (
        <div className="contact-form">
          <h2>{currentContact._id ? 'Edit Contact' : 'Add Contact'}</h2>
          <form onSubmit={handleFormSubmit}>
            <label>
              Name:
              <input
                type="text"
                name="name"
                value={currentContact.name}
                onChange={handleFormChange}
                required
              />
            </label>
            <label>
              Email:
              <input
                type="email"
                name="email"
                value={currentContact.email}
                onChange={handleFormChange}
                required
              />
            </label>
            <label>
              Phone:
              <input
                type="text"
                name="phone"
                value={currentContact.phone}
                onChange={handleFormChange}
              />
            </label>
            <label>
              Company:
              <input
                type="text"
                name="company"
                value={currentContact.company}
                onChange={handleFormChange}
              />
            </label>
            <label>
              Position:
              <input
                type="text"
                name="position"
                value={currentContact.position}
                onChange={handleFormChange}
              />
            </label>
            <label>
              Tags (comma-separated):
              <input
                type="text"
                name="tags"
                value={currentContact.tags.join(', ')}
                onChange={handleFormChange}
              />
            </label>
            <label>
              Lead Score:
              <input
                type="number"
                name="leadScore"
                value={currentContact.leadScore}
                onChange={handleFormChange}
                min="0"
                max="100"
              />
            </label>
            <label>
              Preferred Contact Method:
              <select
                name="preferredContactMethod"
                value={currentContact.preferredContactMethod}
                onChange={handleFormChange}
              >
                <option value="Email">Email</option>
                <option value="Phone">Phone</option>
                <option value="SMS">SMS</option>
                <option value="In-Person">In-Person</option>
              </select>
            </label>
            <label>
              Type:
              <select
                name="type"
                value={currentContact.type}
                onChange={handleFormChange}
                required
              >
                <option value="lead">Lead</option>
                <option value="contact">Contact</option>
              </select>
            </label>
            <label>
              Status:
              <select
                name="status"
                value={currentContact.status}
                onChange={handleFormChange}
              >
                <option value="New">New</option>
                <option value="In Progress">In Progress</option>
                <option value="Converted">Converted</option>
                <option value="Lost">Lost</option>
              </select>
            </label>
            <label>
              Notes:
              <textarea
                name="notes"
                value={currentContact.notes}
                onChange={handleFormChange}
              />
            </label>
            <button type="submit">Save</button>
            <button type="button" onClick={() => setIsEditing(false)}>Cancel</button>
          </form>
        </div>
      ) : (
        <>
          {contacts.length > 0 ? (
            <table>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Email</th>
                  <th>Phone</th>
                  <th>Company</th>
                  <th>Position</th>
                  <th>Tags</th>
                  <th>Lead Score</th>
                  <th>Preferred Contact Method</th>
                  <th>Type</th>
                  <th>Status</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {contacts.map((contact) => (
                  <tr key={contact._id}>
                    <td>{contact.name}</td>
                    <td>{contact.email}</td>
                    <td>{contact.phone}</td>
                    <td>{contact.company}</td>
                    <td>{contact.position}</td>
                    <td>{contact.tags.join(', ')}</td>
                    <td>{contact.leadScore}</td>
                    <td>{contact.preferredContactMethod}</td>
                    <td>{contact.type}</td>
                    <td>{contact.status}</td>
                    <td>
                      <button onClick={() => handleContactClick(contact)}>Edit</button>
                      <button onClick={() => handleDeleteContact(contact._id)}>Delete</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p>No contacts found.</p>
          )}
        </>
      )}
    </div>
  );
};

export default ContactList;
