import React from 'react';

const EmailSummary = ({ emails }) => {
  const unresolvedEmails = emails.filter((email) => email.status === 'Follow-Up Required');
  const priorityEmails = emails.filter((email) => email.priority);

  return (
    <div>
      <h2>Email Summary</h2>
      <p>Total Emails: {emails.length}</p>
      <p>Unresolved Emails: {unresolvedEmails.length}</p>
      <p>Priority Emails: {priorityEmails.length}</p>
    </div>
  );
};

export default EmailSummary;
