import React, { useState, useEffect } from 'react';
import './ContactList.css';
import axios from 'axios';

const ContactList = () => {
  const [contacts, setContacts] = useState([]);
  const [selectedContact, setSelectedContact] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [showPopup, setShowPopup] = useState(false);
  const [isTaskFormVisible, setIsTaskFormVisible] = useState(false);
  const [currentTask, setCurrentTask] = useState({
    title: '',
    dueDate: '',
    description: '',
    status: '',
  });

  useEffect(() => {
    const fetchContacts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/contacts');
        setContacts(response.data);
      } catch (error) {
        console.error('Error fetching contacts:', error);
      }
    };

    fetchContacts();
  }, []);

  const fetchTasksForContact = async (contactId) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/contacts/${contactId}/tasks`);
      setTasks(response.data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  const handleViewContact = (contact) => {
    setSelectedContact(contact);
    fetchTasksForContact(contact._id);
    setShowPopup(true);
  };

  const handleClosePopup = () => {
    setShowPopup(false);
    setSelectedContact(null);
    setTasks([]);
    setIsTaskFormVisible(false);  // Hide the task form when closing the popup
  };

  const handleAddTask = () => {
    setIsTaskFormVisible(true);
  };

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setCurrentTask((prev) => ({ ...prev, [name]: value }));
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        `http://localhost:5000/api/contacts/${selectedContact._id}/tasks`,
        currentTask
      );
      setTasks((prev) => [...prev, response.data]);
      setIsTaskFormVisible(false);  // Hide form after submitting
      setCurrentTask({
        title: '',
        dueDate: '',
        description: '',
        status: '',
      });
    } catch (error) {
      console.error('Failed to add task:', error);
    }
  };

  const handleDeleteContact = async (id) => {
    if (window.confirm('Are you sure you want to delete this contact?')) {
      try {
        await axios.delete(`http://localhost:5000/api/contacts/${id}`);
        setContacts((prev) => prev.filter((contact) => contact._id !== id));
        handleClosePopup();
      } catch (error) {
        console.error('Error deleting contact:', error);
      }
    }
  };

  const handleDeleteTask = async (taskId) => {
    if (window.confirm('Are you sure you want to delete this task?')) {
      try {
        await axios.delete(`http://localhost:5000/api/tasks/${taskId}`);
        setTasks((prev) => prev.filter((task) => task._id !== taskId));
      } catch (error) {
        console.error('Error deleting task:', error);
      }
    }
  };

  return (
    <div className="contact-list">
      <h1>Contact List</h1>
      <div className="contact-table">
        {contacts.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Phone</th>
                <th>Email</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {contacts.map((contact) => (
                <tr key={contact._id}>
                  <td>{contact.name}</td>
                  <td>{contact.phone}</td>
                  <td>{contact.email}</td>
                  <td>
                    <button onClick={() => handleViewContact(contact)}>View</button>
                    <button onClick={() => handleDeleteContact(contact._id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No contacts available.</p>
        )}
      </div>

      {showPopup && selectedContact && (
        <div className="popup">
          <div className="popup-content">
            <button className="close-button" onClick={handleClosePopup}>
              &times;
            </button>
            <h2>Contact Details</h2>
            <p>
              <strong>Name:</strong> {selectedContact.name}
            </p>
            <p>
              <strong>Phone:</strong> {selectedContact.phone}
            </p>
            <p>
              <strong>Email:</strong> {selectedContact.email}
            </p>
            <p>
              <strong>Notes:</strong> {selectedContact.notes}
            </p>

            <h2>Tasks</h2>
            {tasks.length > 0 ? (
              <ul>
                {tasks.map((task) => (
                  <li key={task._id}>
                    <p>
                      <strong>Title:</strong> {task.title}
                    </p>
                    <p>
                      <strong>Due Date:</strong> {task.dueDate}
                    </p>
                    <p>
                      <strong>Status:</strong> {task.status}
                    </p>
                    <button onClick={() => handleDeleteTask(task._id)}>Delete Task</button>
                  </li>
                ))}
              </ul>
            ) : (
              <p>No tasks available for this contact.</p>
            )}

            <button className="delete-contact-button" onClick={() => handleDeleteContact(selectedContact._id)}>
              Delete Contact
            </button>

            <button className="add-task-button" onClick={handleAddTask}>
              Add Task
            </button>

            {isTaskFormVisible && (
              <div className="task-form">
                <h3>Add Task</h3>
                <form onSubmit={handleFormSubmit}>
                  <label>
                    Title:
                    <input
                      type="text"
                      name="title"
                      value={currentTask.title}
                      onChange={handleFormChange}
                      required
                    />
                  </label>
                  <label>
                    Due Date:
                    <input
                      type="date"
                      name="dueDate"
                      value={currentTask.dueDate || ''}
                      onChange={handleFormChange}
                      required
                    />
                  </label>
                  <label>
                    Description:
                    <textarea
                      name="description"
                      value={currentTask.description}
                      onChange={handleFormChange}
                    />
                  </label>
                  <label>
                    Status:
                    <select
                      name="status"
                      value={currentTask.status}
                      onChange={handleFormChange}
                      required
                    >
                      <option value="">Select Status</option>
                      <option value="Pending">Pending</option>
                      <option value="In Progress">In Progress</option>
                      <option value="Completed">Completed</option>
                    </select>
                  </label>
                  <button type="submit">Save Task</button>
                  <button type="button" onClick={() => setIsTaskFormVisible(false)}>
                    Cancel
                  </button>
                </form>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default ContactList;
