import React, { useState, useEffect } from 'react';
import './TaskManager.css';
import axios from 'axios';

const TaskManager = () => {
  const [tasks, setTasks] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [currentTask, setCurrentTask] = useState({
    title: '',
    dueDate: '',
    description: '',
    status: '',
  });
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('');

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/tasks');
      setTasks(response.data);
    } catch (error) {
      console.error('Failed to fetch tasks:', error);
    }
  };

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setCurrentTask((prev) => ({ ...prev, [name]: value }));
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      let response;
      if (currentTask._id) {
        // Edit task
        response = await axios.put(
          `http://localhost:5000/api/tasks/${currentTask._id}`,
          currentTask
        );
        // Update the task in the list
        setTasks((prevTasks) =>
          prevTasks.map((task) =>
            task._id === response.data._id ? response.data : task
          )
        );
      } else {
        // Add new task
        response = await axios.post('http://localhost:5000/api/tasks', currentTask);
        // Add the new task to the tasks list
        setTasks((prevTasks) => [...prevTasks, response.data]);
      }
      resetForm();
    } catch (error) {
      console.error('Failed to save task:', error);
    }
  };

  const handleAddTask = () => {
    setCurrentTask({ title: '', dueDate: '', description: '', status: '' });
    setIsEditing(true);
  };

  const handleTaskClick = (task) => {
    setCurrentTask({ ...task });
    setIsEditing(true);
  };

  const handleDeleteTask = async (id) => {
    if (window.confirm('Are you sure you want to delete this task?')) {
      try {
        await axios.delete(`http://localhost:5000/api/tasks/${id}`);
        setTasks((prev) => prev.filter((task) => task._id !== id));
      } catch (error) {
        console.error('Failed to delete task:', error);
      }
    }
  };

  const resetForm = () => {
    setIsEditing(false);
    setCurrentTask({ title: '', dueDate: '', description: '', status: '' });
  };

  const filteredTasks = tasks
    .filter((task) =>
      task.title.toLowerCase().includes(searchTerm.toLowerCase())
    )
    .sort((a, b) => {
      if (sortBy === 'dueDate') return new Date(a.dueDate) - new Date(b.dueDate);
      if (sortBy === 'status') return a.status.localeCompare(b.status);
      return 0;
    });

  return (
    <div className="task-manager">
      <h1>Task Manager</h1>
      <div className="toolbar">
        <button onClick={handleAddTask} className="add-task-button">Add Task</button>
        <input
          type="text"
          placeholder="Search tasks..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <select onChange={(e) => setSortBy(e.target.value)} value={sortBy}>
          <option value="">Sort By</option>
          <option value="dueDate">Due Date</option>
          <option value="status">Status</option>
        </select>
      </div>

      {isEditing ? (
        <div className="task-form">
          <h2>{currentTask._id ? 'Edit Task' : 'Add Task'}</h2>
          <form onSubmit={handleFormSubmit}>
            <label>
              Title:
              <input
                type="text"
                name="title"
                value={currentTask.title}
                onChange={handleFormChange}
                required
              />
            </label>
            <label>
              Due Date:
              <input
                type="date"
                name="dueDate"
                value={currentTask.dueDate || ''}
                onChange={handleFormChange}
                required
              />
            </label>
            <label>
              Description:
              <textarea
                name="description"
                value={currentTask.description}
                onChange={handleFormChange}
              />
            </label>
            <label>
              Status:
              <select
                name="status"
                value={currentTask.status}
                onChange={handleFormChange}
                required
              >
                <option value="">Select Status</option>
                <option value="Pending">Pending</option>
                <option value="In Progress">In Progress</option>
                <option value="Completed">Completed</option>
              </select>
            </label>
            <div className="form-actions">
              <button type="submit">Save</button>
              <button type="button" onClick={resetForm}>Cancel</button>
            </div>
          </form>
        </div>
      ) : (
        <div className="task-table">
          {filteredTasks.length > 0 ? (
            <table>
              <thead>
                <tr>
                  <th>Title</th>
                  <th>Due Date</th>
                  <th>Description</th>
                  <th>Status</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredTasks.map((task) => (
                  <tr key={task._id}>
                    <td onClick={() => handleTaskClick(task)}>{task.title}</td>
                    <td>{task.dueDate}</td>
                    <td>{task.description}</td>
                    <td>{task.status}</td>
                    <td>
                      <button onClick={() => handleDeleteTask(task._id)}>Delete</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p>No tasks found.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default TaskManager;
