import React, { useState, useEffect } from 'react';
import './ContactList.css';
import axios from 'axios';

const ContactList = () => {
  const [contacts, setContacts] = useState([]);
  const [selectedContact, setSelectedContact] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [showPopup, setShowPopup] = useState(false);
  const [isContactFormVisible, setIsContactFormVisible] = useState(false);
  const [isTaskFormVisible, setIsTaskFormVisible] = useState(false);
  const [currentContact, setCurrentContact] = useState({
    name: '',
    phone: '',
    email: '',
    notes: '',
  });
  const [currentTask, setCurrentTask] = useState({
    title: '',
    dueDate: '',
    description: '',
    status: '',
  });
  const [isEditingContact, setIsEditingContact] = useState(false);
  const [isEditingTask, setIsEditingTask] = useState(false);

  useEffect(() => {
    const fetchContacts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/contacts');
        setContacts(response.data);
      } catch (error) {
        console.error('Error fetching contacts:', error);
      }
    };

    fetchContacts();
  }, []);

  const fetchTasksForContact = async (contactId) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/contacts/${contactId}/tasks`);
      setTasks(response.data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  const handleViewContact = (contact) => {
    setSelectedContact(contact);
    fetchTasksForContact(contact._id);
    setShowPopup(true);
  };

  const handleClosePopup = () => {
    setShowPopup(false);
    setSelectedContact(null);
    setTasks([]);
    setIsTaskFormVisible(false);
    setIsContactFormVisible(false);
  };

  const handleAddContact = () => {
    setCurrentContact({ name: '', phone: '', email: '', notes: '' });
    setIsEditingContact(false);
    setIsContactFormVisible(true);
  };

  const handleEditContact = (contact) => {
    setCurrentContact(contact);
    setIsEditingContact(true);
    setIsContactFormVisible(true);
  };

  const handleDeleteContact = async (id) => {
    if (window.confirm('Are you sure you want to delete this contact?')) {
      try {
        await axios.delete(`http://localhost:5000/api/contacts/${id}`);
        setContacts((prev) => prev.filter((contact) => contact._id !== id));
        handleClosePopup();
      } catch (error) {
        console.error('Error deleting contact:', error);
      }
    }
  };

  const handleAddTask = () => {
    setCurrentTask({ title: '', dueDate: '', description: '', status: '' });
    setIsEditingTask(false);
    setIsTaskFormVisible(true);
  };

  const handleEditTask = (task) => {
    setCurrentTask(task);
    setIsEditingTask(true);
    setIsTaskFormVisible(true);
  };

  const handleDeleteTask = async (taskId) => {
    if (window.confirm('Are you sure you want to delete this task?')) {
      try {
        await axios.delete(`http://localhost:5000/api/tasks/${taskId}`);
        setTasks((prev) => prev.filter((task) => task._id !== taskId));
      } catch (error) {
        console.error('Error deleting task:', error);
      }
    }
  };

  const handleContactFormChange = (e) => {
    const { name, value } = e.target;
    setCurrentContact((prev) => ({ ...prev, [name]: value }));
  };

  const handleContactFormSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isEditingContact) {
        const response = await axios.put(
          `http://localhost:5000/api/contacts/${currentContact._id}`,
          currentContact
        );
        setContacts((prev) =>
          prev.map((contact) =>
            contact._id === response.data._id ? response.data : contact
          )
        );
      } else {
        const response = await axios.post('http://localhost:5000/api/contacts', currentContact);
        setContacts((prev) => [...prev, response.data]);
      }
      setIsContactFormVisible(false);
    } catch (error) {
      console.error('Error saving contact:', error);
    }
  };

  const handleTaskFormChange = (e) => {
    const { name, value } = e.target;
    setCurrentTask((prev) => ({ ...prev, [name]: value }));
  };

  const handleTaskFormSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isEditingTask) {
        const response = await axios.put(
          `http://localhost:5000/api/tasks/${currentTask._id}`,
          currentTask
        );
        setTasks((prev) =>
          prev.map((task) => (task._id === response.data._id ? response.data : task))
        );
      } else {
        const response = await axios.post(
          `http://localhost:5000/api/contacts/${selectedContact._id}/tasks`,
          currentTask
        );
        setTasks((prev) => [...prev, response.data]);
      }
      setIsTaskFormVisible(false);
    } catch (error) {
      console.error('Error saving task:', error);
    }
  };

  return (
    <div className="contact-list">
      <h1>Contact List</h1>
      <button onClick={handleAddContact}>Add Contact</button>
      <div className="contact-table">
        {contacts.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Phone</th>
                <th>Email</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {contacts.map((contact) => (
                <tr key={contact._id}>
                  <td>{contact.name}</td>
                  <td>{contact.phone}</td>
                  <td>{contact.email}</td>
                  <td>
                    <button onClick={() => handleViewContact(contact)}>View</button>
                    <button onClick={() => handleEditContact(contact)}>Edit</button>
                    <button onClick={() => handleDeleteContact(contact._id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No contacts available.</p>
        )}
      </div>

      {isContactFormVisible && (
        <div className="form-popup">
          <h2>{isEditingContact ? 'Edit Contact' : 'Add Contact'}</h2>
          <form onSubmit={handleContactFormSubmit}>
            <label>
              Name:
              <input
                type="text"
                name="name"
                value={currentContact.name}
                onChange={handleContactFormChange}
                required
              />
            </label>
            <label>
              Phone:
              <input
                type="text"
                name="phone"
                value={currentContact.phone}
                onChange={handleContactFormChange}
                required
              />
            </label>
            <label>
              Email:
              <input
                type="email"
                name="email"
                value={currentContact.email}
                onChange={handleContactFormChange}
                required
              />
            </label>
            <label>
              Notes:
              <textarea
                name="notes"
                value={currentContact.notes}
                onChange={handleContactFormChange}
              />
            </label>
            <button type="submit">Save</button>
            <button type="button" onClick={() => setIsContactFormVisible(false)}>
              Cancel
            </button>
          </form>
        </div>
      )}

      {showPopup && selectedContact && (
        <div className="popup">
          <div className="popup-content">
            <button className="close-button" onClick={handleClosePopup}>
              &times;
            </button>
            <h2>Contact Details</h2>
            <p>
              <strong>Name:</strong> {selectedContact.name}
            </p>
            <p>
              <strong>Phone:</strong> {selectedContact.phone}
            </p>
            <p>
              <strong>Email:</strong> {selectedContact.email}
            </p>
            <p>
              <strong>Notes:</strong> {selectedContact.notes}
            </p>
            <h2>Tasks</h2>
            {tasks.length > 0 ? (
              <ul>
                {tasks.map((task) => (
                  <li key={task._id}>
                    <p>
                      <strong>Title:</strong> {task.title}
                    </p>
                    <p>
                      <strong>Due Date:</strong> {task.dueDate}
                    </p>
                    <p>
                      <strong>Status:</strong> {task.status}
                    </p>
                    <button onClick={() => handleEditTask(task)}>Edit Task</button>
                    <button onClick={() => handleDeleteTask(task._id)}>Delete Task</button>
                  </li>
                ))}
              </ul>
            ) : (
              <p>No tasks available for this contact.</p>
            )}
            <button className="add-task-button" onClick={handleAddTask}>
              Add Task
            </button>
          </div>
        </div>
      )}

      {isTaskFormVisible && (
        <div className="form-popup">
          <h3>{isEditingTask ? 'Edit Task' : 'Add Task'}</h3>
          <form onSubmit={handleTaskFormSubmit}>
            <label>
              Title:
              <input
                type="text"
                name="title"
                value={currentTask.title}
                onChange={handleTaskFormChange}
                required
              />
            </label>
            <label>
              Due Date:
              <input
                type="date"
                name="dueDate"
                value={currentTask.dueDate}
                onChange={handleTaskFormChange}
                required
              />
            </label>
            <label>
              Description:
              <textarea
                name="description"
                value={currentTask.description}
                onChange={handleTaskFormChange}
              />
            </label>
            <label>
              Status:
              <select
                name="status"
                value={currentTask.status}
                onChange={handleTaskFormChange}
                required
              >
                <option value="">Select Status</option>
                <option value="Pending">Pending</option>
                <option value="In Progress">In Progress</option>
                <option value="Completed">Completed</option>
              </select>
            </label>
            <button type="submit">Save Task</button>
            <button type="button" onClick={() => setIsTaskFormVisible(false)}>
              Cancel
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default ContactList;
