import React, { useState, useEffect } from 'react';
import './TaskManager.css';
import axios from 'axios';

const TaskManager = () => {
  const [tasks, setTasks] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [currentTask, setCurrentTask] = useState({
    title: '',
    dueDate: '',
    description: '',
    status: '',
  });

  // Fetch tasks from the server
  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/tasks');
        setTasks(response.data);
      } catch (error) {
        console.error('Failed to fetch tasks:', error);
      }
    };
    fetchTasks();
  }, []);

  // Handle form input change
  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setCurrentTask((prev) => ({ ...prev, [name]: value }));
  };

  // Handle form submission for adding/editing tasks
  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = currentTask._id
        ? await axios.put(`http://localhost:5000/api/tasks/${currentTask._id}`, currentTask) // Edit task
        : await axios.post('http://localhost:5000/api/tasks', currentTask); // Add new task

      if (currentTask._id) {
        // Update the task list with the updated task
        setTasks((prev) =>
          prev.map((task) => (task._id === response.data._id ? response.data : task))
        );
      } else {
        // Add the new task to the list
        setTasks((prev) => [...prev, response.data]);
      }

      setIsEditing(false);
      setCurrentTask({ title: '', dueDate: '', description: '', status: '' });
    } catch (error) {
      console.error('Failed to save task:', error);
    }
  };

  // Start editing an existing task
  const handleTaskClick = (task) => {
    setCurrentTask({ ...task });
    setIsEditing(true);
  };

  // Start adding a new task
  const handleAddTask = () => {
    setCurrentTask({ title: '', dueDate: '', description: '', status: '' });
    setIsEditing(true);
  };

  // Handle task deletion
  const handleDeleteTask = async (id) => {
    if (window.confirm('Are you sure you want to delete this task?')) {
      try {
        await axios.delete(`http://localhost:5000/api/tasks/${id}`);
        setTasks((prev) => prev.filter((task) => task._id !== id));
      } catch (error) {
        console.error('Failed to delete task:', error);
      }
    }
  };

  // Render the form for adding or editing a task
  const renderTaskForm = () => (
    <div className="task-form">
      <h2>{currentTask._id ? 'Edit Task' : 'Add Task'}</h2>
      <form onSubmit={handleFormSubmit}>
        <label>
          Title:
          <input
            type="text"
            name="title"
            value={currentTask.title}
            onChange={handleFormChange}
            required
          />
        </label>
        <label>
          Due Date:
          <input
            type="date"
            name="dueDate"
            value={currentTask.dueDate || ''}
            onChange={handleFormChange}
            required
          />
        </label>
        <label>
          Description:
          <textarea
            name="description"
            value={currentTask.description}
            onChange={handleFormChange}
          />
        </label>
        <label>
          Status:
          <select
            name="status"
            value={currentTask.status}
            onChange={handleFormChange}
            required
          >
            <option value="">Select Status</option>
            <option value="Pending">Pending</option>
            <option value="In Progress">In Progress</option>
            <option value="Completed">Completed</option>
          </select>
        </label>
        <button type="submit">Save</button>
        <button type="button" onClick={() => setIsEditing(false)}>
          Cancel
        </button>
      </form>
    </div>
  );

  // Render the task table
  const renderTaskTable = () => (
    <table>
      <thead>
        <tr>
          <th>Title</th>
          <th>Due Date</th>
          <th>Description</th>
          <th>Status</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {tasks.map((task) => (
          <tr key={task._id}>
            <td onClick={() => handleTaskClick(task)}>{task.title}</td>
            <td onClick={() => handleTaskClick(task)}>{task.dueDate}</td>
            <td onClick={() => handleTaskClick(task)}>{task.description}</td>
            <td onClick={() => handleTaskClick(task)}>{task.status}</td>
            <td>
              <button onClick={() => handleDeleteTask(task._id)}>Delete</button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );

  return (
    <div className="task-manager">
      <h1>Task Manager</h1>
      <button onClick={handleAddTask} className="add-task-button">
        Add Task
      </button>

      {isEditing ? renderTaskForm() : renderTaskTable()}
    </div>
  );
};

export default TaskManager;
