import React, { useEffect, useState } from 'react';
import { Bar } from 'react-chartjs-2';
import { fetchLeads } from '../api';

const LeadGraph = () => {
  const [graphData, setGraphData] = useState(null); // Initialize as null
  const [loading, setLoading] = useState(true); // For better user experience

  useEffect(() => {
    const getLeadData = async () => {
      try {
        const response = await fetchLeads();
        const leads = response.data;

        // Count leads by status
        const statusCounts = leads.reduce((acc, lead) => {
          acc[lead.status] = (acc[lead.status] || 0) + 1;
          return acc;
        }, {});

        setGraphData({
          labels: Object.keys(statusCounts),
          datasets: [
            {
              label: 'Leads by Status',
              data: Object.values(statusCounts),
              backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0'],
            },
          ],
        });
      } catch (error) {
        console.error("Error fetching leads data:", error);
      } finally {
        setLoading(false);
      }
    };

    getLeadData();
  }, []);

  if (loading) return <p>Loading...</p>; // Show a loading state
  if (!graphData) return <p>No data available</p>; // Handle empty data gracefully

  return (
    <div>
      <h2>Lead Status Distribution</h2>
      <Bar data={graphData} />
    </div>
  );
};

export default LeadGraph;
