import React, { useState, useEffect } from 'react';
import './AddTaskModal.css';

const AddTaskModal = ({ onAddTask, onClose, task }) => {
  const [taskTitle, setTaskTitle] = useState('');
  const [dueDate, setDueDate] = useState('');
  const [contactLink, setContactLink] = useState('');

  useEffect(() => {
    if (task) {
      setTaskTitle(task.title);
      setDueDate(task.dueDate === 'No due date' ? '' : task.dueDate);
      setContactLink(task.contactLink === 'No link' ? '' : task.contactLink);
    }
  }, [task]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (taskTitle) {
      const updatedTask = {
        title: taskTitle,
        dueDate: dueDate || 'No due date',
        contactLink: contactLink || 'No link',
      };
      onAddTask(updatedTask);
    }
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <h2>{task ? 'Edit Task' : 'Add Task'}</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Task Title"
            value={taskTitle}
            onChange={(e) => setTaskTitle(e.target.value)}
            required
          />
          <input
            type="date"
            placeholder="Due Date"
            value={dueDate}
            onChange={(e) => setDueDate(e.target.value)}
          />
          <input
            type="text"
            placeholder="Link to Contact (optional)"
            value={contactLink}
            onChange={(e) => setContactLink(e.target.value)}
          />
          <button type="submit">{task ? 'Update Task' : 'Save Task'}</button>
          <button type="button" className="cancel-button" onClick={onClose}>
            Cancel
          </button>
        </form>
      </div>
    </div>
  );
};

export default AddTaskModal;
