import React, { useState, useEffect } from 'react';
import './ContactList.css';
import axios from 'axios';

const ContactList = () => {
  const [contacts, setContacts] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [currentContact, setCurrentContact] = useState({
    name: '',
    email: '',
    phone: '',
    company: '',
    position: '',
    notes: '',
    type: 'lead', // Default type
    leadScore: '', // Default empty
    lastInteraction: '', // Default empty
    createDate: '', // Default empty
  });
  const [sortConfig, setSortConfig] = useState({ key: 'name', direction: 'asc' }); // Default sorting by name, ascending

  useEffect(() => {
    const fetchContacts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/contacts');
        setContacts(response.data);
      } catch (error) {
        console.error('Failed to fetch contacts:', error);
      }
    };

    fetchContacts();
  }, []);

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setCurrentContact((prev) => ({ ...prev, [name]: value }));
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    const contactToSave = { ...currentContact };

    // Set default lastInteraction date to the current date if it's not provided
    if (!contactToSave.lastInteraction) {
      contactToSave.lastInteraction = new Date().toISOString(); // Current date and time
    }

    // If it's a new contact, set createDate to the current date as well
    if (!contactToSave.createDate) {
      contactToSave.createDate = contactToSave.lastInteraction;
    }

    try {
      if (currentContact._id) {
        // Update existing contact
        const response = await axios.put(
          `http://localhost:5000/api/contacts/${currentContact._id}`,
          contactToSave
        );
        setContacts((prev) =>
          prev.map((contact) => (contact._id === response.data._id ? response.data : contact))
        );
      } else {
        // Add new contact
        const response = await axios.post('http://localhost:5000/api/contacts', contactToSave);
        setContacts((prev) => [...prev, response.data]);
      }
      setIsEditing(false);
      setCurrentContact({
        name: '',
        email: '',
        phone: '',
        company: '',
        position: '',
        notes: '',
        type: 'lead',
        leadScore: '',
        lastInteraction: new Date().toISOString(), // Set current date for new contact
        createDate: '', // Will be set to now in case of new contact
      });
    } catch (error) {
      console.error('Failed to save contact:', error);
    }
  };

  const handleAddContact = () => {
    setCurrentContact({
      name: '',
      email: '',
      phone: '',
      company: '',
      position: '',
      notes: '',
      type: 'lead',
      leadScore: '',
      lastInteraction: new Date().toISOString(), // Set current date for new contact
      createDate: '', // Default empty to trigger current date
    });
    setIsEditing(true);
  };

  const handleContactClick = (contact) => {
    setCurrentContact(contact);
    setIsEditing(true);
  };

  const handleDeleteContact = async (id) => {
    if (window.confirm('Are you sure you want to delete this contact?')) {
      try {
        await axios.delete(`http://localhost:5000/api/contacts/${id}`);
        setContacts((prev) => prev.filter((contact) => contact._id !== id));
      } catch (error) {
        console.error('Failed to delete contact:', error);
      }
    }
  };

  const handleSort = (key) => {
    let direction = 'asc';
    if (sortConfig.key === key && sortConfig.direction === 'asc') {
      direction = 'desc';
    }
    setSortConfig({ key, direction });
  };

  const sortedContacts = [...contacts].sort((a, b) => {
    if (a[sortConfig.key] < b[sortConfig.key]) {
      return sortConfig.direction === 'asc' ? -1 : 1;
    }
    if (a[sortConfig.key] > b[sortConfig.key]) {
      return sortConfig.direction === 'asc' ? 1 : -1;
    }
    return 0;
  });

  return (
    <div className="contact-list">
      <h1>Contact List</h1>
      <button onClick={handleAddContact} className="add-contact-button">Add Contact</button>

      {isEditing ? (
        <div className="contact-form">
          <h2>{currentContact._id ? 'Contact Details' : 'Add Contact'}</h2>
          <form onSubmit={handleFormSubmit}>
            <label>
              Name:
              <input
                type="text"
                name="name"
                value={currentContact.name}
                onChange={handleFormChange}
                required
              />
            </label>
            <label>
              Email:
              <input
                type="email"
                name="email"
                value={currentContact.email}
                onChange={handleFormChange}
                required
              />
            </label>
            <label>
              Phone:
              <input
                type="text"
                name="phone"
                value={currentContact.phone}
                onChange={handleFormChange}
              />
            </label>
            <label>
              Company:
              <input
                type="text"
                name="company"
                value={currentContact.company}
                onChange={handleFormChange}
              />
            </label>
            <label>
              Type:
              <select
                name="type"
                value={currentContact.type}
                onChange={handleFormChange}
                required
              >
                <option value="lead">Lead</option>
                <option value="contact">Contact</option>
              </select>
            </label>
            <label>
              Position:
              <input
                type="text"
                name="position"
                value={currentContact.position}
                onChange={handleFormChange}
              />
            </label>
            <label>
              Notes:
              <textarea
                name="notes"
                value={currentContact.notes}
                onChange={handleFormChange}
              />
            </label>
            <label>
              Lead Score:
              <input
                type="number"
                name="leadScore"
                value={currentContact.leadScore}
                onChange={handleFormChange}
                min="0"
                max="100"
              />
            </label>
            <label>
              Last Interaction:
              <input
                type="date"
                name="lastInteraction"
                value={currentContact.lastInteraction?.slice(0, 10)} // Format the date
                onChange={handleFormChange}
              />
            </label>
            <button type="submit">Save</button>
            <button type="button" onClick={() => setIsEditing(false)}>Cancel</button>
          </form>
        </div>
      ) : (
        <>
          {sortedContacts.length > 0 ? (
            <table>
              <thead>
                <tr>
                  <th onClick={() => handleSort('name')}>Name</th>
                  <th onClick={() => handleSort('email')}>Email</th>
                  <th onClick={() => handleSort('phone')}>Phone</th>
                  <th onClick={() => handleSort('company')}>Company</th>
                  <th onClick={() => handleSort('type')}>Type</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {sortedContacts.map((contact) => (
                  <tr key={contact._id}>
                    <td onClick={() => handleContactClick(contact)}>{contact.name}</td>
                    <td onClick={() => handleContactClick(contact)}>{contact.email}</td>
                    <td onClick={() => handleContactClick(contact)}>{contact.phone}</td>
                    <td onClick={() => handleContactClick(contact)}>{contact.company}</td>
                    <td onClick={() => handleContactClick(contact)}>{contact.type}</td>
                    <td>
                      <button onClick={() => handleDeleteContact(contact._id)}>Delete</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p>No contacts found.</p>
          )}
        </>
      )}
    </div>
  );
};

export default ContactList;
